import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { getRecipeImageUrl } from '../utils/imageUtils';
import ChatbotModal from '../components/chatbot/ChatbotModal';
import useStableColor from '../hooks/useStableColor';
import './RecipeDetailPage.css';

const RecipeDetailPage = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { currentUser } = useContext(AuthContext);

    const [recipe, setRecipe] = useState(null);
    const [comments, setComments] = useState([]);
    const [commentText, setCommentText] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [chatbotOpen, setChatbotOpen] = useState(false);

    // Xử lý tương tác
    const [liked, setLiked] = useState(false);
    const [saved, setSaved] = useState(false);
    const [likesCount, setLikesCount] = useState(0);
    const [savesCount, setSavesCount] = useState(0);
    const [sharesCount, setSharesCount] = useState(0);

    // Tải dữ liệu công thức
    useEffect(() => {
        const fetchRecipeData = async () => {
            try {
                setLoading(true);
                const response = await fetch(`http://localhost:5000/api/recipes/${id}`);
                if (!response.ok) {
                    throw new Error('Không thể tải công thức');
                }

                const result = await response.json();

                if (result && result.data) {
                    setRecipe(result.data);
                    setLiked(result.data.userInteraction?.liked || false);
                    setSaved(result.data.userInteraction?.saved || false);
                    setLikesCount(result.data.likesCount || 0);
                    setSavesCount(result.data.savesCount || 0);
                    setSharesCount(result.data.shares || 0);

                    // Tải bình luận
                    const commentsResponse = await fetch(`http://localhost:5000/api/comments/recipe/${id}`);
                    if (commentsResponse.ok) {
                        const commentsResult = await commentsResponse.json();
                        setComments(commentsResult.data || []);
                    }
                }
            } catch (err) {
                setError('Không thể tải công thức. Vui lòng thử lại sau.');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };

        fetchRecipeData();
    }, [id]);

    // Xử lý thích công thức
    const handleLike = async () => {
        if (!currentUser) {
            navigate('/login');
            return;
        }

        try {
            const token = localStorage.getItem('auth_token');
            const response = await fetch(`http://localhost:5000/api/recipes/${id}/like`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                const result = await response.json();
                setLiked(result.data.liked);
                setLikesCount(prev => result.data.liked ? prev + 1 : prev - 1);
            }
        } catch (err) {
            console.error('Error liking recipe:', err);
        }
    };

    // Xử lý lưu công thức
    const handleSave = async () => {
        if (!currentUser) {
            navigate('/login');
            return;
        }

        try {
            const token = localStorage.getItem('auth_token');
            const response = await fetch(`http://localhost:5000/api/recipes/${id}/save`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                const result = await response.json();
                setSaved(result.data.saved);
                setSavesCount(prev => result.data.saved ? prev + 1 : prev - 1);
            }
        } catch (err) {
            console.error('Error saving recipe:', err);
        }
    };

    // Xử lý chia sẻ công thức
    const handleShare = async () => {
        try {
            // Copy link to clipboard
            const url = window.location.href;
            await navigator.clipboard.writeText(url);

            // Record share in backend
            const token = localStorage.getItem('auth_token');
            const response = await fetch(`http://localhost:5000/api/recipes/${id}/share`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': token ? `Bearer ${token}` : ''
                }
            });

            if (response.ok) {
                setSharesCount(prev => prev + 1);
            }

            alert('Đã sao chép liên kết vào bộ nhớ tạm!');
        } catch (err) {
            console.error('Error sharing recipe:', err);
            alert('Không thể chia sẻ công thức. Hãy thử lại sau.');
        }
    };

    // Xử lý tải PDF
    const handleDownloadPDF = async () => {
        try {
            const token = localStorage.getItem('auth_token');
            const response = await fetch(`http://localhost:5000/api/recipes/${id}/pdf`, {
                headers: {
                    'Authorization': token ? `Bearer ${token}` : ''
                }
            });

            if (response.ok) {
                const blob = await response.blob();
                const url = window.URL.createObjectURL(blob);
                const a = document.createElement('a');
                a.style.display = 'none';
                a.href = url;
                a.download = `${recipe.title}.pdf`;
                document.body.appendChild(a);
                a.click();
                window.URL.revokeObjectURL(url);
            } else {
                alert('Không thể tải PDF. Hãy thử lại sau.');
            }
        } catch (err) {
            console.error('Error downloading PDF:', err);
            alert('Không thể tải PDF. Hãy thử lại sau.');
        }
    };

    // Xử lý gửi bình luận
    const handleSubmitComment = async (e) => {
        e.preventDefault();

        if (!currentUser) {
            navigate('/login');
            return;
        }

        if (!commentText.trim()) return;

        try {
            const token = localStorage.getItem('auth_token');
            const response = await fetch(`http://localhost:5000/api/comments/recipe/${id}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ text: commentText })
            });

            if (response.ok) {
                const result = await response.json();
                if (result.data) {
                    setComments(prev => [result.data, ...prev]);
                    setCommentText('');
                }
            }
        } catch (err) {
            console.error('Error posting comment:', err);
        }
    };

    // Toggle chatbot
    const toggleChatbot = () => {
        setChatbotOpen(!chatbotOpen);
    };

    // Nếu cần sử dụng màu ổn định:
    const backgroundColor = useStableColor(id);

    if (loading) {
        return <div className="loading-spinner">Đang tải...</div>;
    }

    if (error || !recipe) {
        return (
            <div className="error-container">
                <p>{error || 'Không thể tải công thức'}</p>
                <button onClick={() => navigate(-1)}>Quay lại</button>
            </div>
        );
    }

    return (
        <div className="recipe-detail-page" style={{ backgroundColor }}>
            <div className="recipe-header">
                <h1>{recipe.title}</h1>
                <div className="recipe-meta">
                    <span className="recipe-author">
                        Đăng bởi: {recipe.author_name || 'Ẩn danh'}
                    </span>
                    <span className="recipe-date">
                        {new Date(recipe.created_at).toLocaleDateString('vi-VN')}
                    </span>
                    <span className="recipe-time">
                        Thời gian nấu: {recipe.cooking_time} phút
                    </span>
                </div>
            </div>

            <div className="recipe-image-container">
                <img 
                    src={getRecipeImageUrl(recipe)} 
                    alt={recipe.title}
                    onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(recipe.title)}&size=512&background=random`;
                    }}
                />
            </div>

            {recipe.thoughts && (
                <div className="recipe-thoughts">
                    <h2>Suy nghĩ của tác giả</h2>
                    <p>{recipe.thoughts}</p>
                </div>
            )}

            <div className="recipe-ingredients">
                <h2>Nguyên liệu</h2>
                <ul>
                    {recipe.ingredients && recipe.ingredients.map((ingredient, index) => (
                        <li key={index}>
                            <span className="ingredient-name">{ingredient.name}</span>: 
                            <span className="ingredient-quantity">{ingredient.quantity} {ingredient.unit}</span>
                        </li>
                    ))}
                </ul>
            </div>

            <div className="recipe-steps">
                <h2>Các bước thực hiện</h2>
                <div className="steps-list">
                    {recipe.steps && recipe.steps.map((step, index) => (
                        <div key={index} className="step-item">
                            <h3>Bước {index + 1}</h3>
                            <p>{step.description}</p>

                            {step.image_url && (
                                <div className="step-image">
                                    <img 
                                        src={`http://localhost:5000${step.image_url}`} 
                                        alt={`Bước ${index + 1}`}
                                        onError={(e) => {
                                            e.target.style.display = 'none';
                                        }}
                                    />
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            </div>

            <div className="recipe-actions">
                <button 
                    className={`action-button ${liked ? 'liked' : ''}`} 
                    onClick={handleLike}
                >
                    <i className={`fas fa-heart ${liked ? 'solid' : 'regular'}`}></i>
                    {liked ? 'Đã thích' : 'Thích'} ({likesCount})
                </button>

                <button 
                    className={`action-button ${saved ? 'saved' : ''}`}
                    onClick={handleSave}
                >
                    <i className={`fas fa-bookmark ${saved ? 'solid' : 'regular'}`}></i>
                    {saved ? 'Đã lưu' : 'Lưu'} ({savesCount})
                </button>

                <button 
                    className="action-button"
                    onClick={handleShare}
                >
                    <i className="fas fa-share-alt"></i>
                    Chia sẻ ({sharesCount})
                </button>

                <button 
                    className="action-button"
                    onClick={handleDownloadPDF}
                >
                    <i className="fas fa-file-pdf"></i>
                    Xuất PDF
                </button>

                <button 
                    className="action-button chatbot-button"
                    onClick={toggleChatbot}
                >
                    <i className="fas fa-robot"></i>
                    Hỏi chatbot
                </button>
            </div>

            <div className="recipe-comments">
                <h2>Bình luận ({comments.length})</h2>

                <form className="comment-form" onSubmit={handleSubmitComment}>
                    <textarea 
                        rows="3"
                        value={commentText}
                        onChange={(e) => setCommentText(e.target.value)}
                        placeholder="Viết bình luận của bạn..."
                    ></textarea>
                    <button type="submit" disabled={!commentText.trim()}>Gửi</button>
                </form>

                <div className="comments-list">
                    {comments.length === 0 ? (
                        <p className="no-comments">Chưa có bình luận nào. Hãy là người đầu tiên bình luận!</p>
                    ) : (
                        comments.map(comment => (
                            <div key={comment.id} className="comment-item">
                                <div className="comment-header">
                                    <div className="comment-avatar">
                                        <img 
                                            src={comment.user_picture ? `http://localhost:5000${comment.user_picture}` : `https://ui-avatars.com/api/?name=${encodeURIComponent(comment.user_name)}&size=40&background=random`} 
                                            alt={comment.user_name} 
                                        />
                                    </div>
                                    <div className="comment-author">
                                        <p className="author-name">{comment.user_name}</p>
                                        <p className="comment-date">{new Date(comment.created_at).toLocaleString('vi-VN')}</p>
                                    </div>
                                </div>
                                <div className="comment-content">
                                    <p>{comment.text}</p>
                                    {comment.image_url && (
                                        <div className="comment-image">
                                            <img src={`http://localhost:5000${comment.image_url}`} alt="Comment attachment" />
                                        </div>
                                    )}
                                </div>

                                {comment.reply_count > 0 && (
                                    <button className="view-replies-button">
                                        Xem {comment.reply_count} phản hồi
                                    </button>
                                )}
                            </div>
                        ))
                    )}
                </div>
            </div>

            {chatbotOpen && (
                <ChatbotModal 
                    recipeId={id}
                    recipeTitle={recipe.title}
                    onClose={toggleChatbot}
                />
            )}
        </div>
    );
};

export default RecipeDetailPage;
