import React, { useEffect, useState } from 'react';

const Following = ({ userId }) => {
    const [following, setFollowing] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (!userId) {
            setLoading(false);
            return;
        }
        // Gọi API lấy danh sách following
        fetch(`http://localhost:5000/api/users/${userId}/following`)
            .then(res => res.json())
            .then(data => {
                setFollowing(data.data || []);
                setLoading(false);
            })
            .catch(() => setLoading(false));
    }, [userId]);

    if (!userId) return <div className="no-data">Không có ID người dùng hợp lệ</div>;

    return (
        <div className="following-list">
            <h2>Đang theo dõi</h2>
            {loading ? (
                <p>Loading...</p>
            ) : following.length > 0 ? (
                <ul>
                    {following.map(user => (
                        <li key={user.id}>
                            <img src={user.picture || '/default-avatar.jpg'} alt={user.name} />
                            <span>{user.name}</span>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>Chưa theo dõi ai.</p>
            )}
        </div>
    );
};

const Profile = ({ profileData }) => {
    return (
        <div className="profile">
            <h1>{profileData.name}</h1>
            <Following userId={profileData.id} />
        </div>
    );
};

export default Profile;
