import React, { useState, useEffect, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import RecipeCard from '../components/recipe/RecipeCard';

const HomePage = () => {
    const { currentUser } = useContext(AuthContext);
    const navigate = useNavigate();
    const [userRecipes, setUserRecipes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUserRecipes = async () => {
            try {
                setLoading(true);

                // Nếu không có người dùng hiện tại, điều hướng đến trang đăng nhập
                if (!currentUser) {
                    navigate('/login');
                    return;
                }

                const token = localStorage.getItem('auth_token');
                const response = await fetch(`http://localhost:5000/api/recipes/user/${currentUser.id}/all`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (!response.ok) {
                    throw new Error('Không thể tải công thức');
                }

                const data = await response.json();
                console.log("Fetched recipes in HomePage:", data);
                setUserRecipes(data.data || []);

            } catch (err) {
                console.error('Error fetching recipes:', err);
                setError('Không thể tải công thức. Vui lòng thử lại sau.');
            } finally {
                setLoading(false);
            }
        };

        fetchUserRecipes();
    }, [currentUser, navigate]); // Chỉ phụ thuộc vào currentUser và navigate

    const handleCreateRecipe = () => {
        navigate('/create-recipe');
    };

    if (loading) {
        return <div>Đang tải...</div>;
    }

    return (
        <div className="home-page">
            <div className="profile-section">
                <div className="profile-header" onClick={() => navigate(`/profile/${currentUser?.id}`)}>
                    <div className="profile-avatar">
                        {currentUser?.picture ? (
                            <img src={currentUser.picture} alt={currentUser.name} />
                        ) : (
                            <div className="avatar-placeholder">{currentUser?.name?.charAt(0) || "U"}</div>
                        )}
                    </div>

                    <div className="profile-info">
                        <h1>{currentUser?.name || "Người dùng"}</h1>
                        <p>{currentUser?.email || "email@example.com"}</p>
                    </div>
                </div>

                <div className="profile-stats">
                    <div className="stat-item">
                        <span className="stat-label">Người theo dõi:</span>
                        <span className="stat-value">{currentUser?.followerCount || 0}</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-label">Đang theo dõi:</span>
                        <span className="stat-value">{currentUser?.followingCount || 0}</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-label">Công thức:</span>
                        <span className="stat-value">{userRecipes.length || 0}</span>
                    </div>
                </div>

                <div className="profile-actions">
                    <button 
                        className="primary-button"
                        onClick={() => navigate('/create-recipe')}
                    >
                        Tạo công thức mới
                    </button>
                </div>
            </div>

            <div className="recipes-section">
                <h2>Công thức của tôi</h2>
                {error && <p className="error-message">{error}</p>}

                {userRecipes.length === 0 ? (
                    <p className="empty-message">Bạn chưa có công thức nào. Hãy tạo công thức đầu tiên!</p>
                ) : (
                    <div className="recipes-grid">
                        {userRecipes.map(recipe => (
                            <Link to={`/recipe/${recipe.id}`} key={recipe.id}>
                                <RecipeCard 
                                    recipe={recipe} 
                                    onClick={() => navigate(`/recipe/${recipe.id}`)} 
                                />
                            </Link>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default HomePage;
