import React, { useState, useEffect, useContext, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import RecipeCard from '../components/recipe/RecipeCard';
import { API_BASE_URL, ENDPOINTS } from '../config/api';
import ProfileLayout from '../components/profile/ProfileLayout';

const DashboardPage = () => {
  const { currentUser, isAuthenticated, setCurrentUser } = useContext(AuthContext);
  const navigate = useNavigate();

  const [publishedRecipes, setPublishedRecipes] = useState([]);
  const [drafts, setDrafts] = useState([]);
  const [savedRecipes, setSavedRecipes] = useState([]);
  const [loadingRecipes, setLoadingRecipes] = useState(false);
  const [loadingSaved, setLoadingSaved] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('published');
  const [showEditName, setShowEditName] = useState(false);
  const [newName, setNewName] = useState('');
  const [uploading, setUploading] = useState(false);

  // Thống kê
  const [stats, setStats] = useState({
    followerCount: 0,
    followingCount: 0,
    publishedCount: 0,
    totalLikes: 0,
    totalShares: 0,
    totalSaves: 0,
  });

  const fileInputRef = useRef();

  // Kiểm tra trạng thái đăng nhập
  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
    }
  }, [isAuthenticated, navigate]);

  // Lấy thống kê cá nhân
  useEffect(() => {
    const fetchStats = async () => {
      if (!currentUser?.id) return;
      try {
        const token = localStorage.getItem('auth_token');
        // SỬA endpoint này:
        const res = await fetch(`${API_BASE_URL}/user/${currentUser.id}/stats`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        if (res.ok) {
          const data = await res.json();
          setStats(data.data || {});
        }
      } catch (err) {
        // ignore
      }
    };
    fetchStats();
  }, [currentUser]);

  // Fetch các danh sách bài đăng
  useEffect(() => {
    if (isAuthenticated && currentUser?.id) {
      if (activeTab === 'published') {
        fetchUserRecipes();
      } else if (activeTab === 'draft') {
        fetchDrafts();
      } else if (activeTab === 'saved') {
        fetchSavedRecipes();
      }
    }
    // eslint-disable-next-line
  }, [isAuthenticated, currentUser, activeTab]);

  // Lấy danh sách bài đăng đã đăng
  const fetchUserRecipes = async () => {
    try {
      setLoadingRecipes(true);
      setError(null);
      const token = localStorage.getItem('auth_token');
      // SỬA endpoint này:
      const endpoint = `/user/${currentUser.id}/recipes?status=published`;
      const response = await fetch(API_BASE_URL + endpoint, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      if (!response.ok) throw new Error('Không thể tải bài đăng');
      const data = await response.json();
      setPublishedRecipes(data.data || []);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoadingRecipes(false);
    }
  };

  // Lấy danh sách bản nháp
  const fetchDrafts = async () => {
    try {
      setLoadingRecipes(true);
      setError(null);
      const token = localStorage.getItem('auth_token');
      const endpoint = ENDPOINTS.recipes.drafts;
      const response = await fetch(API_BASE_URL + endpoint, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      if (!response.ok) throw new Error('Không thể tải bản nháp');
      const data = await response.json();
      setDrafts(data.data || []);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoadingRecipes(false);
    }
  };

  // Lấy danh sách bài đã lưu
  const fetchSavedRecipes = async () => {
    try {
      setLoadingSaved(true);
      setError(null);
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`${API_BASE_URL}/recipes/saved`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      if (!response.ok) throw new Error('Không thể tải bài đã lưu');
      const data = await response.json();
      setSavedRecipes(data.data || []);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoadingSaved(false);
    }
  };

  // Đổi tên
  const handleEditName = async () => {
    if (!newName.trim() || newName === currentUser.name) {
      setShowEditName(false);
      return;
    }
    try {
      const token = localStorage.getItem('auth_token');
      const res = await fetch(`${API_BASE_URL}/user/profile`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ name: newName })
      });
      if (res.ok) {
        setCurrentUser({ ...currentUser, name: newName });
        setShowEditName(false);
      }
    } catch (err) {
      // ignore
    }
  };

  // Đổi avatar
  const handleChangeAvatar = () => {
    fileInputRef.current.click();
  };
  const handleAvatarFileChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setUploading(true);
    try {
      const token = localStorage.getItem('auth_token');
      const formData = new FormData();
      formData.append('avatar', file);
      const res = await fetch(`${API_BASE_URL}/user/upload-avatar`, {
        method: 'POST',
        headers: { 'Authorization': `Bearer ${token}` },
        body: formData
      });
      if (res.ok) {
        const data = await res.json();
        setCurrentUser({ ...currentUser, picture: data.data.picture });
      }
    } catch (err) {
      // ignore
    } finally {
      setUploading(false);
    }
  };

  // Xem danh sách follower/following
  const handleShowFollowers = () => navigate('/followers');
  const handleShowFollowing = () => navigate('/following');

  if (!currentUser) return <div>Đang tải...</div>;

  // Avatar URL
  const avatarUrl = currentUser.picture
    ? currentUser.picture.startsWith('http')
      ? currentUser.picture
      : `http://localhost:5000${currentUser.picture}`
    : `https://ui-avatars.com/api/?name=${encodeURIComponent(currentUser.name || 'User')}&background=random`;

  // Nếu tài khoản bị khóa, chỉ chủ sở hữu mới xem được bài đăng
  const isSuspended = currentUser.is_suspended;

  return (
    <ProfileLayout
      user={currentUser}
      isOwner={true}
      onEditProfile={handleEditProfile}
    />
  );
};

export default DashboardPage;
