import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './ProfilePage.css';

const ProfilePage = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { currentUser } = useContext(AuthContext);
    const [profileData, setProfileData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        const fetchProfileData = async () => {
            try {
                setLoading(true);
                // Fetch profile data
                const response = await fetch(`http://localhost:5000/api/users/${id}`);
                
                if (!response.ok) {
                    throw new Error('Không thể tải thông tin người dùng');
                }
                
                const data = await response.json();
                setProfileData(data);
                
                // Fetch user recipes
                const recipesResponse = await fetch(`http://localhost:5000/api/recipes/user/${id}`);
                
                if (recipesResponse.ok) {
                    const recipesData = await recipesResponse.json();
                    setRecipes(recipesData.recipes || []);
                }
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };
        
        fetchProfileData();
    }, [id]);

    if (loading) {
        return <div className="loading">Đang tải...</div>;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    if (!profileData) {
        return <div className="error-message">Không tìm thấy người dùng</div>;
    }

    return (
        <div className="profile-page">
            <div className="profile-header">
                <img 
                    src={profileData.picture || '/default-avatar.jpg'} 
                    alt={profileData.name} 
                    className="profile-avatar"
                />
                <div className="profile-info">
                    <h1>{profileData.name}</h1>
                    <p>{profileData.email}</p>
                    {currentUser && currentUser.id === profileData.id && (
                        <button 
                            className="edit-profile-button"
                            onClick={() => navigate('/profile/edit')}
                        >
                            Chỉnh sửa thông tin
                        </button>
                    )}
                </div>
            </div>
            
            <div className="profile-stats">
                <div className="stat">
                    <span className="stat-value">{profileData.total_recipes || 0}</span>
                    <span className="stat-label">Công thức</span>
                </div>
                <div className="stat">
                    <span className="stat-value">{profileData.total_likes || 0}</span>
                    <span className="stat-label">Lượt thích</span>
                </div>
                <div className="stat">
                    <span className="stat-value">{profileData.total_saves || 0}</span>
                    <span className="stat-label">Lượt lưu</span>
                </div>
            </div>
            
            <div className="user-recipes">
                <h2>Công thức của {profileData.name}</h2>
                
                {recipes.length === 0 ? (
                    <p>Người dùng chưa đăng công thức nào.</p>
                ) : (
                    <div className="recipe-grid">
                        {recipes.map(recipe => (
                            <div key={recipe.id} className="recipe-card" onClick={() => navigate(`/recipe/${recipe.id}`)}>
                                <img 
                                    src={recipe.image_url || '/default-recipe.jpg'} 
                                    alt={recipe.title} 
                                    className="recipe-image"
                                />
                                <h3>{recipe.title}</h3>
                                <div className="recipe-stats">
                                    <span className="recipe-time">{recipe.cooking_time} phút</span>
                                    <span className="recipe-likes">{recipe.total_likes || 0} thích</span>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default ProfilePage;
