import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Followers = ({ userId, onClose }) => {
    const [followers, setFollowers] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (!userId) return;
        setLoading(true);
        fetch(`http://localhost:5000/api/users/${userId}/followers`)
            .then(res => res.json())
            .then(data => {
                setFollowers(data.data || []);
                setLoading(false);
            })
            .catch(() => setLoading(false));
    }, [userId]);

    return (
        <div className="followers-list">
            <h2>Người theo dõi</h2>
            <span>{followers.length} người theo dõi</span>
            {loading ? (
                <p>Đang tải...</p>
            ) : followers.length > 0 ? (
                <ul className="user-list">
                    {followers.map((follower) => (
                        <li key={follower.id} className="user-item">
                            <Link 
                                to={`/profile/${follower.id}`} 
                                className="user-link"
                                onClick={onClose}
                            >
                                <img 
                                    src={follower.picture ? 
                                        (follower.picture.startsWith('http') ? follower.picture : `http://localhost:5000${follower.picture}`) : 
                                        `https://ui-avatars.com/api/?name=${encodeURIComponent(follower.name)}&background=random`
                                    } 
                                    alt={`${follower.name}'s avatar`} 
                                    className="user-avatar"
                                />
                                <span className="user-name">{follower.name}</span>
                            </Link>
                        </li>
                    ))}
                </ul>
            ) : (
                <p className="empty-message">Chưa có người theo dõi nào.</p>
            )}
        </div>
    );
};

export default Followers;
