import React, { useState, useContext, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './LoginPage.css';

const LoginPage = () => {
    const navigate = useNavigate();
    const { isAuthenticated, login } = useContext(AuthContext);

    // Nếu đã đăng nhập, chuyển hướng đến dashboard
    useEffect(() => {
        if (isAuthenticated) {
            navigate('/dashboard');
        }
    }, [isAuthenticated, navigate]);

    // Thêm useEffect để kiểm tra trạng thái đăng nhập khi component mount
    useEffect(() => {
        // Kiểm tra xem có token trong localStorage không
        const token = localStorage.getItem('auth_token');
        const user = localStorage.getItem('user');

        // Nếu có token nhưng chưa được xác thực trong context, xóa để đăng nhập lại
        if (token && !isAuthenticated) {
            localStorage.removeItem('auth_token');
            localStorage.removeItem('user');
        }
        // Nếu đã xác thực, chuyển hướng đến dashboard
        else if (isAuthenticated) {
            navigate('/dashboard');
        }
    }, [isAuthenticated, navigate]);

    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setLoading(true);

        try {
            // Gọi API đăng nhập
            const response = await fetch('http://localhost:5000/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: formData.email,
                    password: formData.password
                })
            });

            const data = await response.json();

            if (response.ok) {
                // Lưu token và user vào localStorage
                localStorage.setItem('auth_token', data.token);
                localStorage.setItem('user', JSON.stringify(data.user));

                // Cập nhật context
                login(data.user, data.token);

                // Điều hướng về dashboard
                navigate('/dashboard');
            } else {
                throw new Error(data.message || 'Đăng nhập thất bại');
            }
        } catch (err) {
            console.error("Login error:", err);
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-page">
            <div className="login-container">
                <h1>Đăng nhập</h1>

                {error && <div className="error-message">{error}</div>}

                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="password">Mật khẩu</label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <button 
                        type="submit" 
                        className="primary-button"
                        disabled={loading}
                    >
                        {loading ? 'Đang xử lý...' : 'Đăng nhập'}
                    </button>
                </form>

                <div className="auth-links">
                    Chưa có tài khoản? <Link to="/register">Đăng ký</Link>
                </div>

                <Link to="/home" className="back-link">
                    &larr; Quay lại trang chủ
                </Link>
            </div>
        </div>
    );
};

export default LoginPage;
