// Kiểm tra xác nhận rằng OtherProfilePage không chứa các thành phần header hoặc layout
// File này KHÔNG nên import hay sử dụng Header, Footer hoặc MainLayout
import React, { useState, useEffect, useContext } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './ProfilePage.css';
import RecipeCard from '../components/recipe/RecipeCard';
import { generateAvatarUrl } from '../utils/imageUtils';
import Followers from '../components/profile/Followers';
import Following from '../components/profile/Following';

// Component chỉ nên render nội dung, không bao gồm header hoặc footer
const OtherProfilePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { currentUser, isAuthenticated } = useContext(AuthContext);
  
  const [profileData, setProfileData] = useState(null);
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isFollowing, setIsFollowing] = useState(false);
  const [showFollowers, setShowFollowers] = useState(false);
  const [showFollowing, setShowFollowing] = useState(false);
  const [followers, setFollowers] = useState([]);
  
  // Tải thông tin người dùng và công thức của họ
  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        setLoading(true);
        setError(null);
        
        // Lấy token từ localStorage
        const token = localStorage.getItem('auth_token');
        const headers = token ? { 'Authorization': `Bearer ${token}` } : {};
        
        // Lấy thông tin profile
        const profileResponse = await fetch(`http://localhost:5000/api/users/${id}`, {
          headers
        });
        
        if (!profileResponse.ok) {
          throw new Error('Không thể tải thông tin người dùng');
        }
        
        const profileResult = await profileResponse.json();
        setProfileData(profileResult.data || profileResult);
        
        // Kiểm tra xem người dùng hiện tại có theo dõi người dùng này không
        if (isAuthenticated && currentUser) {
          const followResponse = await fetch(`http://localhost:5000/api/users/${id}/follow-status`, {
            headers
          });
          
          if (followResponse.ok) {
            const followData = await followResponse.json();
            setIsFollowing(followData.following || false);
          }
        }
        
        // Lấy công thức của người dùng (chỉ hiển thị công thức đã được duyệt)
        const recipesResponse = await fetch(`http://localhost:5000/api/recipes/users/${id}/public`, {
          headers
        });
        
        if (recipesResponse.ok) {
          const recipesResult = await recipesResponse.json();
          setRecipes(recipesResult.data || []);
        } else {
          console.error('Không thể tải công thức của người dùng');
        }
        
      } catch (err) {
        console.error('Error fetching profile:', err);
        setError('Không thể tải thông tin người dùng. Vui lòng thử lại sau.');
      } finally {
        setLoading(false);
      }
    };
    
    fetchProfileData();
  }, [id, isAuthenticated, currentUser]);
  
  // Xử lý theo dõi/hủy theo dõi người dùng
  const handleFollowToggle = async () => {
    if (!isAuthenticated) {
      navigate('/login', { state: { from: `/profile/${id}` } });
      return;
    }
    
    try {
      const token = localStorage.getItem('token');
      const endpoint = isFollowing ? 'unfollow' : 'follow';
      
      const response = await fetch(`http://localhost:5000/api/users/${id}/${endpoint}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      if (response.ok) {
        setIsFollowing(!isFollowing);
        // Cập nhật lại thông tin người dùng
        setProfileData(prev => ({
          ...prev,
          followerCount: isFollowing ? 
            (prev.followerCount > 0 ? prev.followerCount - 1 : 0) : 
            (prev.followerCount + 1)
        }));
      }
    } catch (err) {
      console.error('Error toggling follow status:', err);
    }
  };
  
  // Xử lý báo cáo người dùng
  const handleReportUser = () => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }
    
    // Mở modal báo cáo
    navigate(`/report/user/${id}`);
  };
  
  // Thêm hàm xử lý khi click vào số người theo dõi
  const handleShowFollowers = async () => {
    try {
      // Fetch danh sách người theo dõi
      const token = localStorage.getItem('auth_token');
      const headers = token ? { 'Authorization': `Bearer ${token}` } : {};
      
      const response = await fetch(`http://localhost:5000/api/users/${id}/followers`, { headers });
      
      if (response.ok) {
        const data = await response.json();
        setFollowers(data.data || []);
        setShowFollowers(true);
      }
    } catch (err) {
      console.error('Error fetching followers:', err);
    }
  };
  
  // Hàm đóng modal
  const closeFollowersModal = () => {
    setShowFollowers(false);
  };
  
  const closeFollowingModal = () => {
    setShowFollowing(false);
  };

  if (loading) {
    return <div className="loading-container">Đang tải thông tin người dùng...</div>;
  }
  
  if (error) {
    return (
      <div className="error-container">
        <h3>Có lỗi xảy ra</h3>
        <p>{error}</p>
        <button onClick={() => window.location.reload()}>Thử lại</button>
      </div>
    );
  }
  
  // Kiểm tra nếu người dùng này là chính mình, chuyển hướng về profile cá nhân
  if (currentUser && profileData && currentUser.id === parseInt(profileData.id)) {
    navigate('/dashboard');
    return null;
  }
  
  if (!profileData) {
    return (
      <div className="error-container">
        <h3>Không tìm thấy người dùng</h3>
        <p>Người dùng này không tồn tại hoặc đã bị khóa.</p>
        <Link to="/" className="btn btn-primary">Quay về trang chủ</Link>
      </div>
    );
  }
  
  console.log('recipes:', recipes);
  
  return (
    <div className="profile-page other-profile-page">
      <div className="profile-header">
        <div className="profile-avatar-container">
          <img 
            src={profileData.picture 
                ? `http://localhost:5000${profileData.picture}` 
                : generateAvatarUrl(profileData.name || 'User')}
            alt={profileData.name} 
            className="profile-avatar"
            onError={(e) => {e.target.src = generateAvatarUrl(profileData.name || 'User')}}
          />
        </div>
        
        <div className="profile-info">
          <div className="profile-name-container">
            <h1>{profileData.name}</h1>
            {profileData.role === 'admin' && 
              <span className="admin-badge">Admin</span>
            }
          </div>
          
          <p className="profile-email">{profileData.email}</p>
          
          {profileData.bio && (
            <p className="profile-bio">{profileData.bio}</p>
          )}
          
          <div className="profile-actions">
            {isAuthenticated && currentUser && currentUser.id !== parseInt(profileData.id) && (
              <button className="follow-button" onClick={handleFollowToggle}>
                {isFollowing ? 'Đang theo dõi' : 'Theo dõi'}
              </button>
            )}
            
            {isAuthenticated && currentUser && currentUser.id !== parseInt(profileData.id) && (
              <button className="message-button" onClick={() => navigate(`/messages/${id}`)}>
                <i className="fas fa-envelope"></i> Nhắn tin
              </button>
            )}
            
            {isAuthenticated && currentUser && currentUser.id !== parseInt(profileData.id) && (
              <button className="report-button" onClick={handleReportUser}>
                <i className="fas fa-flag"></i>
              </button>
            )}
          </div>
        </div>
      </div>
      
      <div className="profile-stats">
        <div className="stat">
          <span className="stat-value">{recipes.length || 0}</span>
          <span className="stat-label">Công thức</span>
        </div>
        <div className="stat" onClick={handleShowFollowers} style={{cursor: 'pointer'}}>
          <span className="stat-value">{profileData.followerCount || 0}</span>
          <span className="stat-label">Người theo dõi</span>
        </div>
        <div className="stat" onClick={() => setShowFollowing(true)} style={{cursor: 'pointer'}}>
          <span className="stat-value">{profileData.followingCount || 0}</span>
          <span className="stat-label">Đang theo dõi</span>
        </div>
      </div>
      
      <h2 className="section-title">Công thức của {profileData.name}</h2>
      
      {recipes.length > 0 ? (
        <div className="recipes-grid">
          {recipes.map(recipe => (
            <div key={recipe.id} className="recipe-link-wrapper">
              <RecipeCard recipe={recipe} onClick={() => navigate(`/recipe/${recipe.id}`)} />
            </div>
          ))}
        </div>
      ) : (
        <div className="empty-recipes">
          <p>Người dùng này chưa đăng công thức nào.</p>
        </div>
      )}
      
      {/* Modal danh sách người theo dõi */}
      {showFollowers && (
        <div className="modal-overlay" onClick={closeFollowersModal}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <button className="close-modal" onClick={closeFollowersModal}>×</button>
            <Followers 
              userId={id} 
              followers={followers}
              onClose={closeFollowersModal}
            />
          </div>
        </div>
      )}
      
      {/* Modal danh sách đang theo dõi */}
      {showFollowing && (
        <div className="modal-overlay" onClick={closeFollowingModal}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <button className="close-modal" onClick={closeFollowingModal}>×</button>
            <Following 
              userId={id}
              onClose={closeFollowingModal}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default OtherProfilePage;
