import api from './api';

export const getAllRecipes = async () => {
    try {
        const response = await api.get('/recipes');
        return response.data;
    } catch (error) {
        throw new Error('Error fetching recipes: ' + error.message);
    }
};

export const getRecipeById = async (id) => {
    try {
        const response = await api.get(`/recipes/${id}`);
        return response.data;
    } catch (error) {
        throw new Error('Error fetching recipe: ' + error.message);
    }
};

export const createRecipe = async (formData) => {
  try {
    // In log để kiểm tra formData
    for (let [key, value] of formData.entries()) {
      if (key !== 'recipe' && !key.startsWith('step_')) {
        console.log(key, value);
      } else {
        console.log(key, "File object");
      }
    }

    // Đảm bảo gửi kèm token xác thực
    const token = localStorage.getItem('auth_token');
    if (!token) {
      throw new Error('Không tìm thấy token xác thực');
    }

    const response = await fetch('http://localhost:5000/api/recipes', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${token}`
      },
      body: formData,
    });

    const data = await response.json();
    console.log("API response:", data);

    if (!response.ok) {
      throw new Error(data.message || 'Có lỗi xảy ra khi tạo công thức');
    }

    return data;
  } catch (error) {
    console.error("Error in createRecipe:", error);
    throw error;
  }
};

export const updateRecipe = async (id, recipeData) => {
    try {
        const response = await api.put(`/recipes/${id}`, recipeData);
        return response.data;
    } catch (error) {
        throw new Error('Error updating recipe: ' + error.message);
    }
};

export const deleteRecipe = async (id) => {
    try {
        const response = await api.delete(`/recipes/${id}`);
        return response.data;
    } catch (error) {
        throw new Error('Error deleting recipe: ' + error.message);
    }
};
