import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { createRecipe } from '../services/recipeService';

const CreateRecipePage = () => {
    const navigate = useNavigate();
    const [title, setTitle] = useState('');
    const [image, setImage] = useState(null);
    const [cookingTime, setCookingTime] = useState('');
    const [thoughts, setThoughts] = useState('');
    const [ingredients, setIngredients] = useState([{ name: 'jnndkndk', quantity: '1', unit: 'g' }]);
    const [steps, setSteps] = useState([{ description: 'njdnkndk' }]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Nếu có dữ liệu đã lưu, khôi phục
        const savedForm = localStorage.getItem('recipeFormData');
        if (savedForm) {
            try {
                const formData = JSON.parse(savedForm);
                setTitle(formData.title || '');
                setCookingTime(formData.cookingTime || '');
                setThoughts(formData.thoughts || '');
                setIngredients(formData.ingredients || [{ name: '', quantity: '', unit: '' }]);
                setSteps(formData.steps || [{ description: '' }]);

                // Đã khôi phục xong, xóa data
                localStorage.removeItem('recipeFormData');
                alert("Đã khôi phục dữ liệu công thức bạn đang soạn thảo!");
            } catch (e) {
                console.error("Error restoring form:", e);
            }
        }
    }, []);

    useEffect(() => {
        // Kiểm tra xem có dữ liệu đã lưu không
        const savedData = localStorage.getItem('pendingRecipeData');
        if (savedData) {
            try {
                const data = JSON.parse(savedData);
                setTitle(data.title || '');
                setCookingTime(data.cookingTime || '');
                setThoughts(data.thoughts || '');
                setIngredients(data.ingredients || [{ name: '', quantity: '', unit: '' }]);
                setSteps(data.steps || [{ description: '' }]);

                // Đã khôi phục xong, xóa data
                localStorage.removeItem('pendingRecipeData');
                alert('Đã khôi phục công thức bạn đang soạn trước đó!');
            } catch (e) {
                console.error('Error restoring recipe data:', e);
            }
        }
    }, []);

    // Tự động lưu form data mỗi 30 giây
    useEffect(() => {
        const autoSaveInterval = setInterval(() => {
            if (title || cookingTime || ingredients.length > 1 || steps.length > 1) {
                const formData = {
                    title,
                    cookingTime,
                    thoughts,
                    ingredients,
                    steps: steps.map(step => ({description: step.description}))
                };
                localStorage.setItem('pendingRecipeData', JSON.stringify(formData));
                console.log('Form data auto-saved');
            }
        }, 30000);

        return () => clearInterval(autoSaveInterval);
    }, [title, cookingTime, thoughts, ingredients, steps]);

    // Xử lý thêm nguyên liệu
    const handleAddIngredient = () => {
        setIngredients([...ingredients, { name: '', quantity: '', unit: '' }]);
    };

    // Xử lý thêm bước
    const handleAddStep = () => {
        setSteps([...steps, { description: '' }]);
    };

    // Xử lý thay đổi nguyên liệu
    const handleIngredientChange = (index, field, value) => {
        const newIngredients = [...ingredients];
        newIngredients[index][field] = value;
        setIngredients(newIngredients);
    };

    // Xử lý thay đổi bước
    const handleStepChange = (index, value) => {
        const newSteps = [...steps];
        newSteps[index].description = value;
        setSteps(newSteps);
    };

    // Xử lý thay đổi hình ảnh bước
    const handleStepImageChange = (index, e) => {
        const file = e.target.files[0];
        if (file) {
            const updatedSteps = [...steps];
            updatedSteps[index].image = file;
            setSteps(updatedSteps);
        }
    };

    // Xử lý thay đổi vị trí bước
    const handleMoveStep = (index, direction) => {
        const newIndex = index + direction;
        if (newIndex < 0 || newIndex >= steps.length) return;

        const updatedSteps = [...steps];
        const step = updatedSteps[index];
        updatedSteps.splice(index, 1);
        updatedSteps.splice(newIndex, 0, step);
        setSteps(updatedSteps);
    };

    // Xử lý thay đổi hình ảnh công thức
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImage(file);
    };

    // Xử lý xóa nguyên liệu
    const handleRemoveIngredient = (index) => {
        const updatedIngredients = [...ingredients];
        updatedIngredients.splice(index, 1);
        setIngredients(updatedIngredients);
    };

    // Xử lý xóa bước
    const handleRemoveStep = (index) => {
        const updatedSteps = [...steps];
        updatedSteps.splice(index, 1);
        setSteps(updatedSteps);
    };

    // Lưu form data vào localStorage
    const saveFormToLocalStorage = () => {
        const formData = {
            title,
            cookingTime,
            thoughts,
            ingredients,
            steps: steps.map(step => ({ description: step.description }))
            // Không thể lưu file objects
        };
        localStorage.setItem('recipeFormData', JSON.stringify(formData));
    };

    // Sửa đổi hàm handleSubmit
    const handleSubmit = async (e) => {
        e.preventDefault(); // Quan trọng! Ngăn chặn việc chuyển hướng trang

        // Kiểm tra người dùng đã đăng nhập chưa
        const token = localStorage.getItem('auth_token');
        if (!token) {
            alert('Bạn cần đăng nhập để đăng công thức');
            navigate('/login');
            return;
        }

        // Kiểm tra dữ liệu
        if (!isDraft && (!title || !cookingTime || ingredients.length === 0 || steps.length === 0)) {
            setError('Vui lòng nhập đầy đủ thông tin bắt buộc');
            return;
        }

        try {
            setLoading(true);
            setError(null);

            // Tạo FormData
            const formData = new FormData();
            formData.append('title', title);
            formData.append('cookingTime', cookingTime);
            formData.append('thoughts', thoughts || '');

            // Log để kiểm tra
            console.log("===== SUBMITTING RECIPE =====");
            console.log("Title:", title);
            console.log("Cooking time:", cookingTime);
            console.log("Ingredients:", JSON.stringify(ingredients));
            console.log("Steps:", JSON.stringify(steps));

            // Đảm bảo ingredients và steps là chuỗi JSON hợp lệ và có dữ liệu
            if (ingredients.length === 0 || !ingredients[0].name) {
                setError('Vui lòng thêm ít nhất một nguyên liệu');
                setLoading(false);
                return;
            }

            if (steps.length === 0 || !steps[0].description) {
                setError('Vui lòng thêm ít nhất một bước nấu');
                setLoading(false);
                return;
            }

            formData.append('ingredients', JSON.stringify(ingredients));
            formData.append('steps', JSON.stringify(steps));

            // Thêm ảnh nếu có
            if (image) {
                formData.append('recipe', image);
            }

            // Gửi yêu cầu trực tiếp thay vì dùng service
            const response = await fetch('http://localhost:5000/api/recipes', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`
                },
                body: formData
            });

            // Log response để debug
            console.log("Response status:", response.status);
            const result = await response.json();
            console.log("Response data:", result);

            if (response.ok) {
                // Xóa dữ liệu form đã lưu
                localStorage.removeItem('recipeFormData');
                localStorage.removeItem('pendingRecipeData');

                // Hiển thị thông báo thành công
                alert('Công thức đã được đăng thành công và đang chờ duyệt!');

                // Chuyển hướng về trang chủ
                navigate('/');
            } else {
                throw new Error(result.message || 'Không thể tạo công thức');
            }
        } catch (err) {
            console.error("Error submitting form:", err);
            setError(`Lỗi: ${err.message}`);
        } finally {
            setLoading(false);
        }
    };

    // Sửa đổi hàm handleSubmitNoAuth
    const handleSubmitNoAuth = async (e) => {
        e.preventDefault();

        // Kiểm tra dữ liệu cơ bản
        if (!title) {
            setError('Vui lòng nhập tên món ăn');
            return;
        }

        try {
            setLoading(true);
            setError(null);

            // Dùng API endpoint test thay vì endpoint chính
            const testData = {
                title,
                cookingTime,
                thoughts,
                ingredients,
                steps
            };

            console.log("TEST SUBMIT: Sending data to test endpoint");
            console.log("Data:", testData);

            // Gửi request đến endpoint test không cần xác thực
            const response = await fetch('http://localhost:5000/api/test/save-recipe', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    title,
                    cookingTime,
                    thoughts,
                    ingredients,
                    steps
                })
            });

            console.log("Response status:", response.status);
            const result = await response.json();
            console.log("Response data:", result);

            if (response.ok) {
                // Xóa dữ liệu form đã lưu
                localStorage.removeItem('recipeFormData');
                localStorage.removeItem('pendingRecipeData');

                // Thông báo thành công
                alert(`Công thức test đã được lưu thành công! ID: ${result.recipeId}`);

                // Chuyển hướng về trang chủ
                navigate('/');
            } else {
                throw new Error(result.message || 'Không thể lưu công thức test');
            }
        } catch (err) {
            console.error("Error in test submission:", err);
            setError(`Lỗi gửi thử nghiệm: ${err.message}`);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="create-recipe-page">
            <h1>Tạo công thức mới</h1>
            {error && <p className="error-message">{error}</p>}

            <form className="recipe-form" onSubmit={e => {
    e.preventDefault(); // Thêm prevent vào đây để đảm bảo
    handleSubmit(e, false);
}}>
                {/* Tên món ăn */}
                <div className="form-group">
                    <label htmlFor="title">Tên món ăn *</label>
                    <input 
                        type="text" 
                        id="title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        placeholder="Nhập tên món ăn"
                        required
                    />
                </div>

                {/* Hình ảnh */}
                <div className="form-group">
                    <label htmlFor="image">Hình ảnh món ăn</label>
                    <input 
                        type="file" 
                        id="image"
                        accept="image/*"
                        onChange={handleImageChange}
                    />
                </div>

                {/* Thời gian nấu */}
                <div className="form-group">
                    <label htmlFor="cookingTime">Thời gian nấu (phút) *</label>
                    <input 
                        type="number" 
                        id="cookingTime"
                        value={cookingTime}
                        onChange={(e) => setCookingTime(e.target.value)}
                        min="1"
                        placeholder="Nhập thời gian nấu"
                        required
                    />
                </div>

                {/* Nguyên liệu */}
                <div className="form-group">
                    <h3>Nguyên liệu *</h3>
                    {ingredients.map((ingredient, index) => (
                        <div key={index} className="ingredient-row">
                            <input
                                type="text"
                                placeholder="Tên nguyên liệu"
                                value={ingredient.name}
                                onChange={(e) => handleIngredientChange(index, 'name', e.target.value)}
                                required
                            />
                            <input
                                type="text"
                                placeholder="Số lượng"
                                value={ingredient.quantity}
                                onChange={(e) => handleIngredientChange(index, 'quantity', e.target.value)}
                                required
                            />
                            <input
                                type="text"
                                placeholder="Đơn vị (g, ml...)"
                                value={ingredient.unit}
                                onChange={(e) => handleIngredientChange(index, 'unit', e.target.value)}
                            />
                        </div>
                    ))}
                    <button type="button" onClick={handleAddIngredient}>
                        Thêm nguyên liệu
                    </button>
                </div>

                {/* Các bước nấu */}
                <div className="form-group">
                    <h3>Các bước *</h3>
                    {steps.map((step, index) => (
                        <div key={index} className="step-row">
                            <textarea
                                placeholder={`Bước ${index + 1}`}
                                value={step.description || ''} 
                                onChange={(e) => handleStepChange(index, e.target.value)}
                                required
                            />
                        </div>
                    ))}
                    <button type="button" onClick={handleAddStep}>
                        Thêm bước
                    </button>
                </div>

                {/* Suy nghĩ và cảm nhận */}
                <div className="form-group">
                    <label htmlFor="thoughts">Suy nghĩ và cảm nhận về món ăn</label>
                    <textarea 
                        id="thoughts"
                        value={thoughts || ''} 
                        onChange={(e) => setThoughts(e.target.value)}
                        placeholder="Chia sẻ câu chuyện, cảm nhận của bạn về món ăn này..."
                        rows={4} 
                    /> 
                </div>

                {/* Nút hành động */}
                <div className="form-actions">
                    <button 
                        type="button" 
                        className="secondary-button"
                        onClick={(e) => handleSubmit(e, true)}
                        disabled={loading}
                    >
                        Lưu nháp
                    </button>

                    <button 
                        type="submit" 
                        className="primary-button"
                        disabled={loading}
                    >
                        {loading ? 'Đang xử lý...' : 'Đăng công thức'}
                    </button>

                    <button 
                        type="button" 
                        onClick={async () => {
                            try {
                                const response = await fetch('http://localhost:5000/api/test/save-recipe', {
                                    method: 'POST',
                                    headers: {
                                        'Content-Type': 'application/json'
                                    },
                                    body: JSON.stringify({
                                        title: "Test Recipe via Direct API",
                                        cookingTime: 30,
                                        ingredients: [{name: "Test ingredient", quantity: "100", unit: "g"}],
                                        steps: [{description: "Test step"}]
                                    })
                                });
                                const result = await response.json();
                                alert(`API Test result: ${JSON.stringify(result)}`);
                            } catch (error) {
                                alert(`API Test error: ${error.message}`);
                            }
                        }}
                    >
                        Test API Directly
                    </button>
                </div>
            </form>
        </div>
    );
};

export default CreateRecipePage;
