import React, { useContext, useEffect, useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { GoogleLogin } from '@react-oauth/google';
import './LoginPage.css';

const LoginPage = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const { isAuthenticated, login } = useContext(AuthContext);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        // Nếu đã đăng nhập, chuyển hướng đến dashboard
        if (isAuthenticated) {
            navigate('/dashboard');
        }

        // Xóa token không hợp lệ nếu có
        const token = localStorage.getItem('token');
        if (token === 'null' || token === 'undefined') {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
        }
    }, [isAuthenticated, navigate]);

    // Thêm timeout cho fetch
    const fetchWithTimeout = async (url, options = {}, timeout = 10000) => {
        const controller = new AbortController();
        const id = setTimeout(() => controller.abort(), timeout);

        try {
            const response = await fetch(url, {
                ...options,
                signal: controller.signal
            });
            clearTimeout(id);
            return response;
        } catch (error) {
            clearTimeout(id);
            if (error.name === 'AbortError') {
                throw new Error('Request timeout');
            }
            throw error;
        }
    };

    // Xử lý đăng nhập Google thành công
    const handleGoogleLoginSuccess = async (credentialResponse) => {
        try {
            setLoading(true);
            setError(null);

            console.log("Google OAuth response received - attempting login...");
            const { credential } = credentialResponse;

            // Thêm Debug thời gian
            try {
                const timeResponse = await fetch('http://localhost:5000/api/auth/time');
                const timeData = await timeResponse.json();
                console.log("Server time:", timeData.timeString);
                console.log("Browser time:", new Date().toString());
            } catch (e) {
                console.error("Could not fetch server time:", e);
            }

            const response = await fetchWithTimeout('http://localhost:5000/api/auth/google', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ tokenId: credential })
            }, 20000); // Tăng timeout lên 20 giây

            const data = await response.json();

            if (data.success) {
                // Lưu token vào localStorage
                localStorage.setItem('token', data.token);
                localStorage.setItem('user', JSON.stringify(data.user));

                console.log("Đăng nhập Google thành công, token:", data.token.substring(0, 15) + "...");

                // Cập nhật trạng thái đăng nhập
                login(data.token, data.user);

                // Chuyển hướng người dùng
                const redirectPath = localStorage.getItem('redirectAfterLogin') || '/dashboard';
                localStorage.removeItem('redirectAfterLogin');
                navigate(redirectPath);
            } else {
                throw new Error(data.message || 'Đăng nhập thất bại');
            }
        } catch (error) {
            console.error("Google login error:", error);

            // Xử lý lỗi chi tiết hơn
            let errorMessage = 'Đăng nhập với Google thất bại';

            if (error.message.includes('Token used too early')) {
                errorMessage = 'Lỗi đồng bộ thời gian giữa máy chủ và Google. Vui lòng thử lại sau.';
            } else if (error.message.includes('Invalid token')) {
                errorMessage = 'Token không hợp lệ. Vui lòng thử lại.';
            } else if (error.message.includes('Network Error')) {
                errorMessage = 'Lỗi kết nối đến máy chủ. Vui lòng kiểm tra kết nối mạng và thử lại.';
            } else if (error.message === 'Request timeout') {
                errorMessage = 'Thời gian yêu cầu hết hạn. Vui lòng thử lại.';
            }

            setError(errorMessage);
        } finally {
            setLoading(false);
        }
    };

    // Xử lý lỗi đăng nhập Google
    const handleGoogleLoginError = () => {
        setError('Đăng nhập với Google thất bại, vui lòng thử lại');
        console.error("Google login failed");
    };

    // Debug button handler (chỉ hiển thị trong môi trường phát triển)
    const handleCheckToken = async () => {
        const token = localStorage.getItem('token');
        const user = localStorage.getItem('user');

        try {
            const response = await fetch('http://localhost:5000/api/auth/me', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                const data = await response.json();
                alert(`
                    Token ('token'): ${token ? token.substring(0, 15) + '...' : 'Không tìm thấy'}
                    User: ${user ? 'Đã lưu' : 'Không tìm thấy'}
                    API Check: Thành công - Đã xác thực là "${data.data?.name || 'Unknown'}"
                `);
            } else {
                alert(`
                    Token ('token'): ${token ? token.substring(0, 15) + '...' : 'Không tìm thấy'}
                    User: ${user ? 'Đã lưu' : 'Không tìm thấy'}
                    API Check: Thất bại - Status ${response.status}
                `);
            }
        } catch (e) {
            alert(`
                Token ('token'): ${token ? token.substring(0, 15) + '...' : 'Không tìm thấy'}
                User: ${user ? 'Đã lưu' : 'Không tìm thấy'}
                API Check: Lỗi - ${e.message}
            `);
        }
    };

    return (
        <div className="login-page">
            <div className="login-container">
                <div className="login-header">
                    <h1>Đăng nhập</h1>
                    <p>Đăng nhập bằng tài khoản Google của bạn để tiếp tục</p>
                </div>

                {error && (
                    <div className="error-message">
                        <i className="fas fa-exclamation-circle"></i>
                        {error}
                    </div>
                )}

                <div className="google-login-wrapper">
                    {loading ? (
                        <div className="loading-spinner">
                            <div className="spinner"></div>
                            <p>Đang đăng nhập...</p>
                        </div>
                    ) : (
                        <GoogleLogin
                            onSuccess={handleGoogleLoginSuccess}
                            onError={handleGoogleLoginError}
                            useOneTap
                            theme="filled_blue"
                            shape="rectangular"
                            text="signin_with"
                            locale="vi"
                            width="300px"
                        />
                    )}
                </div>

                <div className="login-info">
                    <div className="info-item">
                        <i className="fas fa-lock"></i>
                        <p>Đăng nhập an toàn với tài khoản Google</p>
                    </div>
                    <div className="info-item">
                        <i className="fas fa-shield-alt"></i>
                        <p>Chúng tôi không lưu mật khẩu của bạn</p>
                    </div>
                </div>

                <div className="login-footer">
                    <p>Bằng việc đăng nhập, bạn đồng ý với <Link to="/terms">Điều khoản sử dụng</Link> và <Link to="/privacy">Chính sách bảo mật</Link> của chúng tôi</p>
                    <Link to="/home" className="back-home">Quay lại trang chủ</Link>
                </div>

                {/* Debug tools - chỉ hiển thị trong môi trường phát triển */}
                {process.env.NODE_ENV === 'development' && (
                    <div className="debug-tools" style={{marginTop: "20px"}}>
                        <button type="button" onClick={handleCheckToken}>
                            Kiểm tra token
                        </button>
                        <button
                            type="button"
                            onClick={() => {
                                localStorage.removeItem('token');
                                localStorage.removeItem('user');
                                localStorage.removeItem('auth_token'); // Xóa cả token cũ nếu có
                                alert('Đã xóa tất cả token và thông tin người dùng');
                            }}
                        >
                            Xóa tất cả token
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default LoginPage;
