import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { Link } from 'react-router-dom';
import './NotificationsPage.css';

const NotificationsPage = () => {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('all');
  const [unreadCount, setUnreadCount] = useState(0);
  const { currentUser } = useContext(AuthContext);

  // States for reply modal
  const [showReplyModal, setShowReplyModal] = useState(false);
  const [selectedNotification, setSelectedNotification] = useState(null);
  const [replyText, setReplyText] = useState('');
  const [submitting, setSubmitting] = useState(false);

  // States for message to admin modal
  const [showMessageModal, setShowMessageModal] = useState(false);
  const [adminMessage, setAdminMessage] = useState('');

  useEffect(() => {
    fetchNotifications();
  }, []);

  const fetchNotifications = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      
      if (!token) {
        throw new Error('Bạn cần đăng nhập để xem thông báo');
      }
      
      const response = await fetch('http://localhost:5000/api/notifications', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
      if (!response.ok) {
        throw new Error('Không thể tải thông báo');
      }
      
      const data = await response.json();
      setNotifications(data.data || []);
      
      // Đếm số thông báo chưa đọc
      const unread = data.data?.filter(notification => !notification.is_read).length || 0;
      setUnreadCount(unread);
      
    } catch (err) {
      console.error('Error fetching notifications:', err);
      setError('Không thể tải thông báo. ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  // Đánh dấu một thông báo là đã đọc
  const markAsRead = async (notificationId) => {
    try {
      const token = localStorage.getItem('token');
      
      await fetch(`http://localhost:5000/api/notifications/${notificationId}/read`, {
        method: 'POST',
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
      // Cập nhật state local
      setNotifications(notifications.map(notif => 
        notif.id === notificationId ? { ...notif, is_read: true } : notif
      ));
      
      // Giảm số thông báo chưa đọc
      if (unreadCount > 0) setUnreadCount(unreadCount - 1);
      
    } catch (err) {
      console.error('Error marking notification as read:', err);
    }
  };

  // Đánh dấu tất cả thông báo là đã đọc
  const markAllAsRead = async () => {
    try {
      const token = localStorage.getItem('token');
      
      await fetch('http://localhost:5000/api/notifications/read-all', {
        method: 'POST',
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
      // Cập nhật state local
      setNotifications(notifications.map(notif => ({ ...notif, is_read: true })));
      setUnreadCount(0);
      
    } catch (err) {
      console.error('Error marking all notifications as read:', err);
    }
  };
  
  // Xử lý phản hồi tới admin
  const handleReply = (notification) => {
    setSelectedNotification(notification);
    setReplyText('');
    setShowReplyModal(true);
  };
  
  // Gửi phản hồi tới admin
  const submitReply = async (e) => {
    e.preventDefault();
    
    if (!replyText.trim()) return;
    
    try {
      setSubmitting(true);
      
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/notifications/${selectedNotification.id}/reply`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ text: replyText })
      });
      
      if (!response.ok) {
        throw new Error('Không thể gửi phản hồi');
      }
      
      // Đóng modal và làm mới dữ liệu
      setShowReplyModal(false);
      fetchNotifications();
      
    } catch (err) {
      console.error('Error sending reply:', err);
    } finally {
      setSubmitting(false);
    }
  };
  
  // Gửi tin nhắn tới admin
  const submitMessageToAdmin = async (e) => {
    e.preventDefault();
    
    if (!adminMessage.trim()) return;
    
    try {
      setSubmitting(true);
      
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/notifications/message-admin', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ message: adminMessage })
      });
      
      if (!response.ok) {
        throw new Error('Không thể gửi tin nhắn');
      }
      
      // Đóng modal và làm mới dữ liệu
      setShowMessageModal(false);
      setAdminMessage('');
      alert('Tin nhắn của bạn đã được gửi tới quản trị viên!');
      
    } catch (err) {
      console.error('Error sending message to admin:', err);
      alert(`Lỗi: ${err.message}`);
    } finally {
      setSubmitting(false);
    }
  };
  
  // Lọc thông báo theo tab
  const filteredNotifications = notifications.filter(notif => {
    if (activeTab === 'all') return true;
    if (activeTab === 'unread') return !notif.is_read;
    if (activeTab === 'admin') return notif.type === 'admin';
    if (activeTab === 'interaction') {
      return ['like', 'comment', 'save', 'share'].includes(notif.type);
    }
    return true;
  });

  // Hiển thị biểu tượng theo loại thông báo
  const getNotificationIcon = (type) => {
    switch(type) {
      case 'like': return 'fa-heart';
      case 'comment': return 'fa-comment';
      case 'save': return 'fa-bookmark';
      case 'share': return 'fa-share-alt';
      case 'admin': return 'fa-shield-alt';
      case 'report_result': return 'fa-flag';
      default: return 'fa-bell';
    }
  };

  // Render thông báo
  const renderNotification = (notification) => {
    const { id, type, title, content, sender_name, created_at, is_read, resource_id, resource_type } = notification;
    
    let icon = getNotificationIcon(type);
    let linkPath = resource_id ? `/recipe/${resource_id}` : '#';
    let actionButton;
    
    if (type === 'admin') {
      actionButton = (
        <button 
          className="notification-reply-button" 
          onClick={() => handleReply(notification)}
        >
          Phản hồi
        </button>
      );
    }
    
    return (
      <div 
        key={id} 
        className={`notification-item ${!is_read ? 'unread' : ''}`}
        onClick={() => !is_read && markAsRead(id)}
      >
        <div className="notification-icon">
          <i className={`fas ${icon}`}></i>
        </div>
        
        <div className="notification-content">
          <div className="notification-header">
            <h3 className="notification-title">{title}</h3>
            <span className="notification-time">
              {new Date(created_at).toLocaleDateString('vi-VN', {
                hour: '2-digit',
                minute: '2-digit'
              })}
            </span>
          </div>
          
          <p className="notification-text">{content}</p>
          
          {sender_name && (
            <p className="notification-sender">
              <i className="fas fa-user"></i> {sender_name}
            </p>
          )}
          
          <div className="notification-actions">
            {linkPath && (
              <Link to={linkPath} className="notification-view-button">
                Xem chi tiết
              </Link>
            )}
            
            {actionButton}
          </div>
        </div>
        
        {!is_read && (
          <div className="notification-badge">
            <span className="badge"></span>
          </div>
        )}
      </div>
    );
  };
  
  return (
    <div className="notifications-page">
      <div className="notifications-header">
        <h1>Thông báo</h1>
        
        <div className="notifications-actions">
          <button 
            className="mark-all-read"
            onClick={markAllAsRead}
            disabled={unreadCount === 0}
          >
            <i className="fas fa-check-double"></i> Đánh dấu tất cả là đã đọc
          </button>
          
          <button 
            className="message-admin-button"
            onClick={() => setShowMessageModal(true)}
          >
            <i className="fas fa-envelope"></i> Gửi tin nhắn đến Admin
          </button>
        </div>
        
        <div className="notifications-tabs">
          <button 
            className={`tab ${activeTab === 'all' ? 'active' : ''}`}
            onClick={() => setActiveTab('all')}
          >
            Tất cả
          </button>
          <button 
            className={`tab ${activeTab === 'unread' ? 'active' : ''}`}
            onClick={() => setActiveTab('unread')}
          >
            Chưa đọc 
            {unreadCount > 0 && <span className="count-badge">{unreadCount}</span>}
          </button>
          <button 
            className={`tab ${activeTab === 'admin' ? 'active' : ''}`}
            onClick={() => setActiveTab('admin')}
          >
            Admin
          </button>
          <button 
            className={`tab ${activeTab === 'interaction' ? 'active' : ''}`}
            onClick={() => setActiveTab('interaction')}
          >
            Tương tác
          </button>
        </div>
      </div>
      
      <div className="notifications-list">
        {loading ? (
          <div className="loading-container">
            <div className="spinner"></div>
            <p>Đang tải thông báo...</p>
          </div>
        ) : error ? (
          <div className="error-container">
            <i className="fas fa-exclamation-circle"></i>
            <p>{error}</p>
          </div>
        ) : filteredNotifications.length === 0 ? (
          <div className="empty-notifications">
            <i className="far fa-bell-slash"></i>
            <p>Không có thông báo nào</p>
          </div>
        ) : (
          filteredNotifications.map(renderNotification)
        )}
      </div>
      
      {/* Modal phản hồi admin */}
      {showReplyModal && selectedNotification && (
        <div className="modal-overlay">
          <div className="reply-modal">
            <div className="modal-header">
              <h3>Phản hồi tới Admin</h3>
              <button onClick={() => setShowReplyModal(false)} className="close-button">
                <i className="fas fa-times"></i>
              </button>
            </div>
            
            <div className="modal-content">
              <div className="original-message">
                <h4>{selectedNotification.title}</h4>
                <p>{selectedNotification.content}</p>
              </div>
              
              <form onSubmit={submitReply}>
                <div className="form-group">
                  <label htmlFor="reply">Phản hồi của bạn:</label>
                  <textarea 
                    id="reply" 
                    rows="4" 
                    value={replyText}
                    onChange={(e) => setReplyText(e.target.value)}
                    placeholder="Nhập phản hồi của bạn..."
                    required
                  ></textarea>
                </div>
                
                <div className="form-actions">
                  <button 
                    type="button" 
                    className="cancel-button" 
                    onClick={() => setShowReplyModal(false)}
                    disabled={submitting}
                  >
                    Hủy
                  </button>
                  <button 
                    type="submit" 
                    className="submit-button"
                    disabled={submitting || !replyText.trim()}
                  >
                    {submitting ? 'Đang gửi...' : 'Gửi phản hồi'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
      
      {/* Modal gửi tin nhắn tới admin */}
      {showMessageModal && (
        <div className="modal-overlay">
          <div className="message-modal">
            <div className="modal-header">
              <h3>Gửi tin nhắn tới Admin</h3>
              <button onClick={() => setShowMessageModal(false)} className="close-button">
                <i className="fas fa-times"></i>
              </button>
            </div>
            
            <div className="modal-content">
              <form onSubmit={submitMessageToAdmin}>
                <div className="form-group">
                  <label htmlFor="admin-message">Nội dung tin nhắn:</label>
                  <textarea 
                    id="admin-message" 
                    rows="6" 
                    value={adminMessage}
                    onChange={(e) => setAdminMessage(e.target.value)}
                    placeholder="Nhập tin nhắn bạn muốn gửi tới Admin..."
                    required
                  ></textarea>
                </div>
                
                <div className="info-text">
                  <i className="fas fa-info-circle"></i>
                  <p>Tin nhắn này sẽ được gửi trực tiếp tới đội ngũ quản trị viên.</p>
                </div>
                
                <div className="form-actions">
                  <button 
                    type="button" 
                    className="cancel-button" 
                    onClick={() => setShowMessageModal(false)}
                    disabled={submitting}
                  >
                    Hủy
                  </button>
                  <button 
                    type="submit" 
                    className="submit-button"
                    disabled={submitting || !adminMessage.trim()}
                  >
                    {submitting ? 'Đang gửi...' : 'Gửi tin nhắn'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default NotificationsPage;
