import React, { useState, useContext, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './LoginPage.css';

const LoginPage = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const { isAuthenticated, login } = useContext(AuthContext);

    // State cho thông báo xác minh thành công
    const [verificationSuccess, setVerificationSuccess] = useState(false);

    // Kiểm tra xem người dùng có vừa xác minh email thành công không
    useEffect(() => {
        const params = new URLSearchParams(location.search);
        if (params.get('verified') === 'true') {
            setVerificationSuccess(true);
        }
    }, [location]);

    // Nếu đã đăng nhập, chuyển hướng đến dashboard
    useEffect(() => {
        if (isAuthenticated) {
            navigate('/dashboard');
        }
    }, [isAuthenticated, navigate]);

    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [rememberMe, setRememberMe] = useState(false);
    const [unverifiedEmail, setUnverifiedEmail] = useState('');
    const [resendingVerification, setResendingVerification] = useState(false);
    const [resendSuccess, setResendSuccess] = useState(false);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setLoading(true);
        setUnverifiedEmail('');

        try {
            // Gọi API đăng nhập
            const response = await fetch('http://localhost:5000/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: formData.email,
                    password: formData.password
                })
            });

            const data = await response.json();

            if (data.success) {
                // Sử dụng hàm login từ context để lưu token và thông tin user
                login(data.token, data.user);

                // Log để kiểm tra
                console.log("Token saved:", data.token.substring(0, 20) + "...");

                // Đánh dấu thời gian token được lưu
                localStorage.setItem('token_timestamp', Date.now());

                // Kiểm tra xem có đường dẫn redirect không
                const redirectPath = localStorage.getItem('redirectAfterLogin') || '/dashboard';
                localStorage.removeItem('redirectAfterLogin');

                // Chuyển hướng người dùng
                navigate(redirectPath);
            } else {
                // Kiểm tra nếu là lỗi chưa xác minh email
                if (data.message && data.message.includes('chưa được xác minh')) {
                    setUnverifiedEmail(formData.email);
                }
                throw new Error(data.message || 'Đăng nhập thất bại');
            }
        } catch (err) {
            console.error("Login error:", err);
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // Hàm gửi lại email xác minh
    const handleResendVerification = async () => {
        if (!unverifiedEmail) return;

        setResendingVerification(true);
        setResendSuccess(false);

        try {
            const response = await fetch('http://localhost:5000/api/auth/resend-verification', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: unverifiedEmail
                })
            });

            const data = await response.json();

            if (response.ok) {
                setResendSuccess(true);
            } else {
                throw new Error(data.message || 'Không thể gửi lại email xác minh');
            }
        } catch (err) {
            console.error("Resend verification error:", err);
            setError(err.message);
        } finally {
            setResendingVerification(false);
        }
    };

    return (
        <div className="login-page">
            <div className="simple-login-container">
                <div className="login-header">
                    <h1>Đăng nhập</h1>
                    <p>Vui lòng đăng nhập để tiếp tục</p>
                </div>

                {verificationSuccess && (
                    <div className="success-message">
                        <i className="fas fa-check-circle"></i>
                        Xác minh email thành công! Bây giờ bạn có thể đăng nhập.
                    </div>
                )}

                {error && (
                    <div className="error-message">
                        <i className="fas fa-exclamation-circle"></i>
                        {error}
                    </div>
                )}

                {unverifiedEmail && !resendSuccess && (
                    <div className="warning-message">
                        <i className="fas fa-exclamation-triangle"></i>
                        Tài khoản chưa được xác minh. 
                        <button 
                            className="resend-link"
                            onClick={handleResendVerification}
                            disabled={resendingVerification}
                        >
                            {resendingVerification ? 'Đang gửi...' : 'Gửi lại email xác minh'}
                        </button>
                    </div>
                )}

                {resendSuccess && (
                    <div className="success-message">
                        <i className="fas fa-check-circle"></i>
                        Email xác minh đã được gửi lại. Vui lòng kiểm tra hộp thư của bạn.
                    </div>
                )}

                <form className="login-form" onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <div className="input-with-icon">
                            <i className="fas fa-envelope"></i>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                placeholder="Nhập email của bạn"
                                value={formData.email}
                                onChange={handleChange}
                                required
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="password">Mật khẩu</label>
                        <div className="input-with-icon">
                            <i className="fas fa-lock"></i>
                            <input
                                type="password"
                                id="password"
                                name="password"
                                placeholder="Nhập mật khẩu của bạn"
                                value={formData.password}
                                onChange={handleChange}
                                required
                            />
                        </div>
                    </div>

                    <div className="form-options">
                        <div className="remember-me">
                            <input 
                                type="checkbox" 
                                id="rememberMe" 
                                checked={rememberMe}
                                onChange={() => setRememberMe(!rememberMe)}
                            />
                            <label htmlFor="rememberMe">Ghi nhớ đăng nhập</label>
                        </div>
                        <Link to="/forgot-password" className="forgot-password-link">Quên mật khẩu?</Link>
                    </div>

                    <button 
                        type="submit" 
                        className="login-button"
                        disabled={loading}
                    >
                        {loading ? (
                            <>
                                <span className="spinner"></span>
                                Đang xử lý...
                            </>
                        ) : 'Đăng nhập'}
                    </button>
                </form>

                <div className="login-footer">
                    <p>Chưa có tài khoản? <Link to="/register" className="register-link">Đăng ký ngay</Link></p>
                    <Link to="/" className="back-home">Quay lại trang chủ</Link>
                </div>
            </div>
        </div>
    );
};

export default LoginPage;
