import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getRecipeImageUrl, getPlaceholderImageUrl } from '../../utils/imageUtils';
import './RecipeCard.css';

const RecipeCard = ({ recipe }) => {
  const [imageError, setImageError] = useState(false);

  // Tạo URL hình ảnh đầy đủ
  const imageUrl = getRecipeImageUrl(recipe);
  const hasImage = !!imageUrl; // Kiểm tra có URL ảnh không

  // Log hữu ích cho việc debug
  useEffect(() => {
    console.log(`Recipe ${recipe.id} (${recipe.title}) - Has image URL: ${hasImage}`);
  }, [recipe.id, recipe.title, hasImage]);

  // Tạo màu nền ngẫu nhiên nhưng ổn định cho mỗi công thức
  const getBackgroundColor = () => {
    const seed = recipe.id || 0;
    const hue = (seed * 137.5) % 360; // Sử dụng phép nhân để tạo sự phân tán
    return `hsl(${hue}, 70%, 80%)`;
  };

  return (
    <div className="recipe-card">
      <Link to={`/recipe/${recipe.id}`}>
        <div className="recipe-image-container">
          {hasImage && !imageError ? (
            <img
              src={imageUrl}
              alt={recipe.title}
              onError={(e) => {
                console.error(`Image load error for recipe ${recipe.id} (${recipe.title}):`, imageUrl);
                setImageError(true);
              }}
            />
          ) : (
            <div 
              className="recipe-image-placeholder"
              style={{ 
                backgroundColor: getBackgroundColor(),
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                width: '100%',
                height: '100%'
              }}
            >
              <span className="placeholder-text">{recipe.title.charAt(0).toUpperCase()}</span>
            </div>
          )}
        </div>
        <div className="recipe-info">
          <h3>{recipe.title}</h3>
          <p>{recipe.cooking_time || recipe.cookingTime || 0} phút</p>
        </div>
      </Link>
    </div>
  );
};

const RecipeGrid = ({ recipes }) => {
  return (
    <div className="recipe-grid">
      {recipes.map(recipe => (
        <Link to={`/recipe/${recipe.id}`} key={recipe.id}>
          <div className="recipe-card">
            <div className="recipe-image">
              {recipe.image_url ? (
                <img 
                  src={getRecipeImageUrl(recipe)}
                  alt={recipe.title}
                  onError={(e) => {
                    console.error(`Failed to load image for recipe: ${recipe.id}`, recipe.image_url);
                    // Chỉ thay đổi sang placeholder nếu thật sự không tải được ảnh
                    e.target.src = getPlaceholderImageUrl(recipe.title || 'Recipe');
                  }}
                />
              ) : (
                <div 
                  className="recipe-placeholder"
                  style={{
                    backgroundColor: `hsl(${recipe.id * 137.5 % 360}, 70%, 80%)`,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    width: '100%',
                    height: '100%',
                    color: 'white',
                    fontSize: '2rem'
                  }}
                >
                  {recipe.title.charAt(0).toUpperCase()}
                </div>
              )}
            </div>
            <div className="recipe-content">
              <h3>{recipe.title}</h3>
              <p>{recipe.cooking_time} phút</p>
            </div>
          </div>
        </Link>
      ))}
    </div>
  );
};

export default RecipeCard;
export { RecipeGrid };
