import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { getRecipeImageUrl, generateRecipeImageUrl } from '../../utils/imageUtils';
import useStableColor from '../../hooks/useStableColor';
import './RecipeCard.css';

const RecipeCard = ({ recipe }) => {
  const [imageLoaded, setImageLoaded] = useState(false);
  const [imageError, setImageError] = useState(false);

  // Sử dụng hook để có màu nền ổn định
  const backgroundColor = useStableColor(recipe.id);

  return (
    <div className="recipe-card">
      <Link to={`/recipe/${recipe.id}`}>
        <div className="recipe-image">
          {!imageError ? (
            <img 
              src={getRecipeImageUrl(recipe)}
              alt={recipe.title}
              onLoad={() => setImageLoaded(true)}
              onError={() => {
                console.log(`Ảnh lỗi cho recipe: ${recipe.title}, URL: ${getRecipeImageUrl(recipe)}`);
                setImageError(true);
              }}
              style={{ display: imageLoaded && !imageError ? 'block' : 'none' }}
            />
          ) : null}

          {(imageError || !imageLoaded) && (
            <div 
              className="recipe-placeholder"
              style={{ backgroundColor }}
            >
              <span>{recipe.title.charAt(0).toUpperCase()}</span>
            </div>
          )}
        </div>
        <div className="recipe-info">
          <h3>{recipe.title}</h3>
          <p>{recipe.cookingTime || 0} phút</p>
        </div>
      </Link>
    </div>
  );
};

export default RecipeCard;
