// Tạo file mới với các hàm tiện ích

// Sinh màu ngẫu nhiên cho ảnh đại diện và ảnh công thức
export const getRandomColor = () => {
  const colors = [
    '#FF6B6B', '#FF8E8E', '#FFA5A5', '#FFBDBD',  // Shades of red
    '#4ECDC4', '#70DBCE', '#92E9D9', '#B4F7E4',  // Shades of teal
    '#FFD166', '#FFD980', '#FFE199', '#FFEAB3',  // Shades of yellow
    '#6B5B95', '#8675A9', '#A18FC0', '#BBA9D7',  // Shades of purple
    '#88D8B0', '#A1E1C1', '#BBE9D3', '#D4F2E4',  // Shades of mint
    '#F38181', '#F59E9E', '#F7BCBC', '#F9DADA',  // Shades of salmon
  ];
  
  return colors[Math.floor(Math.random() * colors.length)];
};

// Hàm tạo ảnh đại diện từ tên người dùng
export const generateAvatarUrl = (name) => {
  const initials = name
    .split(' ')
    .map(word => word[0])
    .join('')
    .toUpperCase()
    .substring(0, 2);
  
  const backgroundColor = encodeURIComponent('FF6B6B');
  const textColor = encodeURIComponent('FFFFFF');
  
  return `https://ui-avatars.com/api/?name=${encodeURIComponent(initials)}&background=${backgroundColor}&color=${textColor}&size=256&font-size=0.5&bold=true`;
};

// Hàm xử lý URL hình ảnh cho công thức
export const getRecipeImageUrl = (recipe) => {
  // Nếu không có ảnh hoặc đường dẫn rỗng
  if (!recipe.image_url || recipe.image_url === '') {
    return generateRecipeImageUrl(recipe.title || 'Recipe');
  }
  
  // Nếu đường dẫn đã có http/https, trả về nguyên bản
  if (recipe.image_url.startsWith('http')) {
    return recipe.image_url;
  }
  
  // Thêm tiền tố server
  return `http://localhost:5000${recipe.image_url}`;
};

// Hàm tạo ảnh món ăn mặc định từ tên món ăn
export const generateRecipeImageUrl = (title) => {
  const backgroundColor = encodeURIComponent('FF9A8B');
  const textColor = encodeURIComponent('FFFFFF');
  
  return `https://ui-avatars.com/api/?name=${encodeURIComponent(title)}&background=${backgroundColor}&color=${textColor}&size=256&font-size=0.33&bold=true`;
};
