import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './ProfilePage.css';

const ProfilePage = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { currentUser } = useContext(AuthContext);
    
    const [profileData, setProfileData] = useState(null);
    const [recipes, setRecipes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProfileData = async () => {
            try {
                setLoading(true);
                setError(null);
                
                // Lấy token từ localStorage
                const token = localStorage.getItem('auth_token');
                const headers = token ? { 'Authorization': `Bearer ${token}` } : {};
                
                // Log để debug
                console.log("Fetching profile for ID:", id);
                console.log("Current user:", currentUser);
                console.log("Token exists:", !!token);
                
                // Lấy thông tin profile
                const profileResponse = await fetch(`http://localhost:5000/api/users/${id}`, {
                    headers
                });
                
                if (!profileResponse.ok) {
                    throw new Error('Không thể tải thông tin người dùng');
                }
                
                const profileData = await profileResponse.json();
                setProfileData(profileData.data || profileData);
                console.log("Profile data loaded:", profileData);
                
                // QUAN TRỌNG: sử dụng API getUserRecipes thay vì all-by-user
                // API này có sẵn trong controller của bạn
                const recipesResponse = await fetch(
                    `http://localhost:5000/api/recipes/user/${id}`, 
                    { headers }
                );
                
                console.log("Recipes response status:", recipesResponse.status);
                const recipesData = await recipesResponse.json();
                console.log("Recipes data:", recipesData);
                
                if (recipesResponse.ok) {
                    // Thêm log chi tiết để phát hiện vấn đề
                    console.log("Recipe data structure:", JSON.stringify(recipesData, null, 2));
                    
                    // Kiểm tra cấu trúc của API response
                    if (recipesData.data) {
                        setRecipes(recipesData.data);
                    } else if (Array.isArray(recipesData)) {
                        setRecipes(recipesData);
                    } else {
                        console.error("Unexpected data structure:", recipesData);
                        setRecipes([]);
                    }
                } else {
                    console.error("Failed to load recipes:", recipesData);
                    throw new Error(recipesData.message || 'Không thể tải công thức');
                }
            } catch (err) {
                console.error("Error loading profile data:", err);
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };
        
        fetchProfileData();
    }, [id, currentUser]);

    const handleRetry = () => {
        window.location.reload();
    };

    // Thêm nút tạo công thức test (xóa sau khi debug xong)
    const createTestRecipe = async () => {
        try {
            const token = localStorage.getItem('auth_token');
            const response = await fetch('http://localhost:5000/api/test/create-recipe', {
                headers: {
                    'Authorization': token ? `Bearer ${token}` : ''
                }
            });
            const data = await response.json();
            alert(`Đã tạo công thức test: ${JSON.stringify(data)}`);
            window.location.reload();
        } catch (error) {
            alert(`Lỗi: ${error.message}`);
        }
    };

    if (loading) {
        return <div className="loading">Đang tải...</div>;
    }

    return (
        <div className="profile-page">
            <div className="profile-header">
                <img 
                    src={profileData?.picture || '/default-avatar.jpg'} 
                    alt={profileData?.name} 
                    className="profile-avatar"
                    onError={(e) => {e.target.src = '/default-avatar.jpg'}}
                />
                <div className="profile-info">
                    <h1>{profileData?.name || 'Người dùng'}</h1>
                    <p>{profileData?.email || ''}</p>
                    {currentUser && profileData && currentUser.id === parseInt(profileData.id) && (
                        <button 
                            className="edit-profile-button"
                            onClick={() => navigate('/profile/edit')}
                        >
                            Chỉnh sửa thông tin
                        </button>
                    )}
                </div>
            </div>
            
            <div className="profile-stats">
                <div className="stat">
                    <span className="stat-value">{recipes?.length || 0}</span>
                    <span className="stat-label">Công thức</span>
                </div>
                <div className="stat">
                    <span className="stat-value">{profileData?.total_likes || 0}</span>
                    <span className="stat-label">Lượt thích</span>
                </div>
                <div className="stat">
                    <span className="stat-value">{profileData?.total_saves || 0}</span>
                    <span className="stat-label">Lượt lưu</span>
                </div>
            </div>
            
            <div className="user-recipes">
                <h2>Công thức của {profileData?.name || 'tôi'}</h2>
                
                {/* Chỉ hiển thị debug info khi trong môi trường dev */}
                {process.env.NODE_ENV !== 'production' && (
                    <div className="debug-buttons">
                        <button onClick={createTestRecipe} className="debug-button">
                            Tạo công thức test
                        </button>
                        <button onClick={() => console.log("Current recipes:", recipes)} className="debug-button">
                            Log recipes
                        </button>
                    </div>
                )}
                
                {error ? (
                    <div className="error-message">
                        {error}
                        <button 
                            onClick={handleRetry}
                            className="retry-button"
                        >
                            Thử lại
                        </button>
                    </div>
                ) : recipes?.length === 0 ? (
                    <div>
                        <p>Chưa có công thức nào.</p>
                        {currentUser && profileData && currentUser.id === parseInt(profileData.id) && (
                            <button 
                                className="create-recipe-button"
                                onClick={() => navigate('/create-recipe')}
                            >
                                Tạo công thức mới
                            </button>
                        )}
                    </div>
                ) : (
                    <div className="recipe-grid">
                        {recipes.map(recipe => (
                            <div key={recipe.id} className="recipe-card" onClick={() => navigate(`/recipe/${recipe.id}`)}>
                                <img 
                                    src={recipe.image_url ? `http://localhost:5000${recipe.image_url}` : '/default-recipe.jpg'} 
                                    alt={recipe.title} 
                                    className="recipe-image"
                                    onError={(e) => {e.target.src = '/default-recipe.jpg'}}
                                />
                                <h3>{recipe.title}</h3>
                                <div className="recipe-info">
                                    <span>{recipe.cooking_time} phút</span>
                                    <span className={`recipe-status ${recipe.status}`}>
                                        {recipe.status === 'published' ? 'Đã xuất bản' : 
                                         recipe.status === 'pending_review' ? 'Đang chờ duyệt' : 
                                         recipe.status === 'draft' ? 'Bản nháp' : 'Bị từ chối'}
                                    </span>
                                </div>
                                <div className="recipe-stats">
                                    <span title="Lượt thích"><i className="fa fa-heart"></i> {recipe.likes_count || 0}</span>
                                    <span title="Lượt lưu"><i className="fa fa-bookmark"></i> {recipe.saves_count || 0}</span>
                                    <span title="Lượt chia sẻ"><i className="fa fa-share"></i> {recipe.shares || 0}</span>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default ProfilePage;
