import React, { useState, useEffect } from 'react';
import { Link, useSearchParams, useNavigate } from 'react-router-dom';
import './SearchResultsPage.css';
import RecipeCard from '../components/recipe/RecipeCard';

const SearchResultsPage = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const navigate = useNavigate();

  // State để lưu trữ kết quả tìm kiếm
  const [recipeResults, setRecipeResults] = useState([]);
  const [userResults, setUserResults] = useState([]);
  const [activeTab, setActiveTab] = useState('recipes'); // 'recipes' hoặc 'users'
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // State cho bộ lọc
  const [filters, setFilters] = useState({
    category: searchParams.get('category') || '',
    ingredient: searchParams.get('ingredient') || '',
    time: searchParams.get('time') || '',
    difficulty: searchParams.get('difficulty') || ''
  });

  // Lấy query từ URL
  const query = searchParams.get('q') || '';

  // Tải kết quả tìm kiếm khi params thay đổi
  useEffect(() => {
    // Lấy tab từ URL params nếu có
    const tabParam = searchParams.get('tab');
    if (tabParam && (tabParam === 'recipes' || tabParam === 'users')) {
      setActiveTab(tabParam);
    }

    const fetchSearchResults = async () => {
      setLoading(true);
      setError(null);

      try {
        // Lấy tất cả params từ URL
        const searchOptions = {
          query,
          category: searchParams.get('category'),
          ingredient: searchParams.get('ingredient'),
          time: searchParams.get('time'),
          difficulty: searchParams.get('difficulty')
        };

        console.log('Searching with options:', searchOptions);

        // Tìm kiếm công thức
        if (activeTab === 'recipes') {
          try {
            // Tìm kiếm với Typesense
            const recipeIndex = searchClient.initIndex('recipes');

            // Xây dựng filters cho Typesense
            const filters = [];
            if (searchOptions.category) {
              filters.push(`category:${searchOptions.category}`);
            }
            if (searchOptions.difficulty) {
              filters.push(`difficulty:${searchOptions.difficulty}`);
            }
            if (searchOptions.time) {
              if (searchOptions.time === 'quick') {
                filters.push('cooking_time:<30');
              } else if (searchOptions.time === 'medium') {
                filters.push('cooking_time:>=30 && cooking_time:<=60');
              } else if (searchOptions.time === 'long') {
                filters.push('cooking_time:>60');
              }
            }

            const searchParams = {
              query: searchOptions.query || '',
              filters: filters.join(' && '),
              hitsPerPage: 50
            };

            const results = await recipeIndex.search(searchParams.query, searchParams);
            setRecipeResults(results.hits);
          } catch (typesenseError) {
            console.error('Typesense search error:', typesenseError);

            // Fallback sang API cũ
            const response = await fetch(`http://localhost:5000/api/search/recipes?query=${encodeURIComponent(query)}${
              searchOptions.category ? `&category=${searchOptions.category}` : ''}${
              searchOptions.ingredient ? `&ingredient=${searchOptions.ingredient}` : ''}${
              searchOptions.time ? `&time=${searchOptions.time}` : ''}${
              searchOptions.difficulty ? `&difficulty=${searchOptions.difficulty}` : ''
            }`);

            if (!response.ok) {
              throw new Error('Không thể tìm kiếm công thức');
            }

            const recipesResult = await response.json();
            console.log('Search result data:', recipesResult);
            setRecipeResults(recipesResult.data || []);
          }
        }

        // Tìm kiếm người dùng
        if (activeTab === 'users') {
          try {
            // Tìm kiếm với Typesense
            const userIndex = searchClient.initIndex('users');
            const results = await userIndex.search(query, {
              hitsPerPage: 20
            });
            setUserResults(results.hits);
          } catch (typesenseError) {
            console.error('Typesense user search error:', typesenseError);

            // Fallback sang API cũ
            const userResponse = await fetch(`http://localhost:5000/api/search/users?query=${encodeURIComponent(query)}`);

            if (!userResponse.ok) {
              throw new Error('Không thể tìm kiếm người dùng');
            }

            const usersResult = await userResponse.json();
            setUserResults(usersResult.data || []);
          }
        }
      } catch (err) {
        console.error('Search error details:', err);
        setError('Có lỗi xảy ra khi tìm kiếm. Vui lòng thử lại sau.');
      } finally {
        setLoading(false);
      }
    };

    if (query) {
      fetchSearchResults();
    }
  }, [activeTab, query, searchParams]);

  // Xử lý thay đổi filters
  const handleFilterChange = (filterName, value) => {
    const newFilters = { ...filters, [filterName]: value };
    setFilters(newFilters);

    // Cập nhật URL params
    searchParams.set(filterName, value);
    if (!value) searchParams.delete(filterName);
    setSearchParams(searchParams);
  };

  // Xử lý thay đổi tab
  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="search-results-page">
      <h1>Kết quả tìm kiếm: {query}</h1>

      {/* Thanh tìm kiếm */}
      <div className="search-bar">
        <input
          type="text"
          defaultValue={query}
          placeholder="Tìm kiếm..."
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              navigate(`/search?q=${encodeURIComponent(e.target.value)}`);
            }
          }}
        />
      </div>

      {/* Tab điều hướng */}
      <div className="search-tabs">
        <button 
          className={activeTab === 'recipes' ? 'active' : ''} 
          onClick={() => handleTabChange('recipes')}
        >
          Công thức
        </button>
        <button 
          className={activeTab === 'users' ? 'active' : ''} 
          onClick={() => handleTabChange('users')}
        >
          Người dùng
        </button>
      </div>

      {/* Hiển thị lỗi nếu có */}
      {error && <div className="error-message">{error}</div>}

      {/* Hiển thị phần loading */}
      {loading && <div className="loading-spinner">Đang tìm kiếm...</div>}

      {/* Kết quả tìm kiếm công thức */}
      {activeTab === 'recipes' && !loading && (
        <div className="recipes-results">
          {/* Filters */}
          <div className="search-filters">
            <div className="filter-group">
              <label>Danh mục:</label>
              <select 
                value={filters.category}
                onChange={(e) => handleFilterChange('category', e.target.value)}
              >
                <option value="">Tất cả</option>
                <option value="breakfast">Bữa sáng</option>
                <option value="lunch">Bữa trưa</option>
                <option value="dinner">Bữa tối</option>
                <option value="dessert">Tráng miệng</option>
                <option value="snack">Ăn vặt</option>
              </select>
            </div>

            <div className="filter-group">
              <label>Nguyên liệu chính:</label>
              <input
                type="text"
                placeholder="Ví dụ: trứng"
                value={filters.ingredient}
                onChange={(e) => handleFilterChange('ingredient', e.target.value)}
              />
            </div>

            <div className="filter-group">
              <label>Thời gian:</label>
              <select 
                value={filters.time}
                onChange={(e) => handleFilterChange('time', e.target.value)}
              >
                <option value="">Tất cả</option>
                <option value="quick">Nhanh (&lt; 30 phút)</option>
                <option value="medium">Trung bình (30-60 phút)</option>
                <option value="long">Lâu (&gt; 60 phút)</option>
              </select>
            </div>
          </div>

          {/* Kết quả */}
          {recipeResults.length > 0 ? (
            <div className="recipes-grid">
              {recipeResults.map(recipe => (
                <Link to={`/recipe/${recipe.id}`} key={recipe.id}>
                  <RecipeCard recipe={recipe} />
                </Link>
              ))}
            </div>
          ) : (
            <div className="no-results">
              <p>Không tìm thấy công thức nào phù hợp với từ khóa này.</p>
            </div>
          )}
        </div>
      )}

      {/* Kết quả tìm kiếm người dùng */}
      {activeTab === 'users' && !loading && (
        <div className="users-results">
          {userResults.length > 0 ? (
            <div className="users-grid">
              {userResults.map(user => (
                <Link to={`/profile/${user.id}`} key={user.id}>
                  <div className="user-card">
                    <img 
                      src={user.picture || '/default-avatar.jpg'} 
                      alt={user.name}
                      className="user-avatar"
                      onError={(e) => {e.target.src = '/default-avatar.jpg'}}
                    />
                    <div className="user-info">
                      <h3>{user.name}</h3>
                      <p>{user.recipe_count} công thức</p>
                      {user.bio && <p className="user-bio">{user.bio}</p>}
                    </div>
                  </div>
                </Link>
              ))}
            </div>
          ) : (
            <div className="no-results">
              <p>Không tìm thấy người dùng nào phù hợp với từ khóa này.</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchResultsPage;
