import React, { useState, useEffect, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './LandingPage.css';

const LandingPage = () => {
    const { isAuthenticated, currentUser } = useContext(AuthContext);
    const navigate = useNavigate();
    const [featuredRecipes, setFeaturedRecipes] = useState([]);
    const [loading, setLoading] = useState(true);

    // Xóa hoặc comment đoạn code này để không tự động chuyển hướng
    /* 
    useEffect(() => {
        if (isAuthenticated && currentUser) {
            navigate('/dashboard');
        }
    }, [isAuthenticated, currentUser, navigate]);
    */

    // Tải các công thức tiêu biểu
    useEffect(() => {
        const fetchFeaturedRecipes = async () => {
            try {
                setLoading(true);
                const response = await fetch('http://localhost:5000/api/recipes/featured');
                if (response.ok) {
                    const data = await response.json();
                    setFeaturedRecipes(data.data || []);
                }
            } catch (error) {
                console.error('Error fetching featured recipes', error);
            } finally {
                setLoading(false);
            }
        };

        fetchFeaturedRecipes();
    }, []);

    // Thêm function để xử lý đăng xuất khi muốn đăng nhập lại
    const handleLoginClick = () => {
        // Nếu đã đăng nhập, hãy đăng xuất trước khi chuyển đến trang đăng nhập
        if (isAuthenticated) {
            // Xóa token và thông tin user
            localStorage.removeItem('auth_token');
            localStorage.removeItem('user');
            // Thực hiện refresh trang hoặc đơn giản là chuyển hướng
            window.location.href = '/login';  // Dùng window.location thay vì navigate để refresh state
        } else {
            // Nếu chưa đăng nhập thì chuyển thẳng đến trang đăng nhập
            navigate('/login');
        }
    };

    return (
        <div className="landing-page">
            <header className="landing-header">
                <div className="logo">Recipe Sharing</div>
                <nav className="landing-nav">
                    <Link to="/home">Trang chủ</Link>
                    <Link to="/about">Giới thiệu</Link>
                    <Link to="/contact">Liên hệ</Link>
                </nav>
                <div className="auth-buttons">
                    <button onClick={handleLoginClick} className="login-btn">Đăng nhập</button>
                    <Link to="/register" className="register-btn">Đăng ký</Link>
                </div>
            </header>

            <section 
                className="hero-section" 
                style={{
                    backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url('/food-hero.jpg')`
                }}
            >
                <div className="hero-content">
                    <h1>Chia sẻ công thức nấu ăn của bạn với cộng đồng</h1>
                    <p>Khám phá, chia sẻ và lưu trữ các công thức nấu ăn tuyệt vời từ khắp nơi trên thế giới</p>
                    <div className="hero-buttons">
                        <Link to="/register" className="primary-button">Bắt đầu ngay</Link>
                        <Link to="/learn-more" className="secondary-button">Tìm hiểu thêm</Link>
                    </div>
                </div>
            </section>

            <section className="featured-recipes">
                <h2>Công thức nổi bật</h2>
                {loading ? (
                    <div className="loading">Đang tải...</div>
                ) : featuredRecipes.length > 0 ? (
                    <div className="recipe-grid">
                        {featuredRecipes.map(recipe => (
                            <div key={recipe.id} className="recipe-card">
                                <img 
                                    src={recipe.image_url ? `http://localhost:5000${recipe.image_url}` : '/default-recipe.jpg'} 
                                    alt={recipe.title}
                                    className="recipe-image"
                                    onError={(e) => { e.target.src = '/default-recipe.jpg' }}
                                />
                                <h3>{recipe.title}</h3>
                                <p>{recipe.cooking_time} phút • Bởi {recipe.author_name}</p>
                                <Link to="/login" className="view-recipe-btn">Xem công thức</Link>
                            </div>
                        ))}
                    </div>
                ) : (
                    <p>Không có công thức nổi bật</p>
                )}
            </section>

            <section className="features-section">
                <h2>Tính năng</h2>
                <div className="features-grid">
                    <div className="feature-card">
                        <div className="feature-icon">🔍</div>
                        <h3>Tìm kiếm công thức</h3>
                        <p>Dễ dàng tìm kiếm các công thức phù hợp với nguyên liệu bạn có</p>
                    </div>
                    <div className="feature-card">
                        <div className="feature-icon">📝</div>
                        <h3>Chia sẻ công thức</h3>
                        <p>Chia sẻ công thức nấu ăn của bạn với cộng đồng</p>
                    </div>
                    <div className="feature-card">
                        <div className="feature-icon">❤️</div>
                        <h3>Lưu công thức yêu thích</h3>
                        <p>Lưu các công thức bạn yêu thích để xem lại sau</p>
                    </div>
                </div>
            </section>

            <footer className="landing-footer">
                <div className="footer-content">
                    <p>&copy; {new Date().getFullYear()} Recipe Sharing App. All rights reserved.</p>
                    <div className="footer-links">
                        <a href="/terms">Điều khoản sử dụng</a>
                        <a href="/privacy">Chính sách bảo mật</a>
                    </div>
                </div>
            </footer>
        </div>
    );
};

export default LandingPage;
