import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { getRecipeImageUrl } from '../../utils/imageUtils';
import './RecipeCard.css';

const RecipeCard = ({ recipe }) => {
  const [imageLoaded, setImageLoaded] = useState(false);
  const [imageError, setImageError] = useState(false);

  // Tạo màu nền ổn định cho placeholder
  const getStableColor = (id) => {
    const colors = [
      '#FF9AA2', '#FFB7B2', '#FFDAC1', '#E2F0CB', '#B5EAD7', 
      '#C7CEEA', '#F8B195', '#F67280', '#C06C84', '#6C5B7B'
    ];

    // Tạo một giá trị từ id
    const seed = id || Math.random() * 1000;
    const colorIndex = Math.floor(seed % colors.length);

    return colors[colorIndex];
  };

  const backgroundColor = getStableColor(recipe.id);

  return (
    <div className="recipe-card">
      <Link to={`/recipe/${recipe.id}`}>
        <div className="recipe-image">
          {!imageError ? (
            <img 
              src={getRecipeImageUrl(recipe)}
              alt={recipe.title}
              onLoad={() => setImageLoaded(true)}
              onError={() => setImageError(true)}
              style={{ display: imageError ? 'none' : 'block' }}
            />
          ) : (
            <div 
              className="recipe-placeholder"
              style={{ backgroundColor }}
            >
              <span>{recipe.title}</span>
            </div>
          )}
        </div>
        <div className="recipe-content">
          <h3>{recipe.title}</h3>
          <div className="recipe-details">
            <span>{recipe.cooking_time} phút</span>
            {recipe.author_name && (
              <span className="recipe-author">by {recipe.author_name}</span>
            )}
          </div>
        </div>
      </Link>
    </div>
  );
};

export default RecipeCard;
