import React, { createContext, useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [currentUser, setCurrentUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Check if user is already logged in (from local storage)
        const checkLoggedIn = () => {
            const token = localStorage.getItem('auth_token');
            if (token) {
                try {
                    const decodedToken = jwtDecode(token);
                    // Check if token is expired
                    if (decodedToken.exp * 1000 < Date.now()) {
                        localStorage.removeItem('auth_token');
                        setCurrentUser(null);
                    } else {
                        setCurrentUser(decodedToken);
                    }
                } catch (error) {
                    localStorage.removeItem('auth_token');
                    setCurrentUser(null);
                }
            }
            setLoading(false);
        };

        checkLoggedIn();
    }, []);

    const loginWithGoogle = async (credential) => {
        try {
            // In a real app, you would send the credential to your backend
            // For now, we'll just decode it and use the info directly
            const decodedCredential = jwtDecode(credential);

            console.log("Google auth successful:", decodedCredential);

            // Set the user in state
            setCurrentUser({
                id: decodedCredential.sub,
                name: decodedCredential.name,
                email: decodedCredential.email,
                picture: decodedCredential.picture
            });

            // Store token in localStorage
            localStorage.setItem('auth_token', credential);

            return decodedCredential;
        } catch (error) {
            console.error("Error in loginWithGoogle:", error);
            throw new Error('Authentication failed');
        }
    };

    const logout = () => {
        localStorage.removeItem('auth_token');
        setCurrentUser(null);
    };

    return (
        <AuthContext.Provider
            value={{
                currentUser,
                loginWithGoogle,
                logout,
                loading
            }}
        >
            {children}
        </AuthContext.Provider>
    );
};
