import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthContext, AuthProvider } from './context/AuthContext';
import { RecipeProvider } from './context/RecipeContext';

// Layouts
import MainLayout from './components/layout/MainLayout';
import AdminLayout from './components/layout/AdminLayout';

// Pages
import LoginPage from './pages/LoginPage';
import HomePage from './pages/HomePage';
import CreateRecipePage from './pages/CreateRecipePage';
import RecipeDetailPage from './pages/RecipeDetailPage';
import SearchResultsPage from './pages/SearchResultsPage';
// @ts-ignore
import ProfilePage from './pages/ProfilePage';
// @ts-ignore
import AdminPage from './pages/AdminPage';
// @ts-ignore
import AdminUsersPage from './pages/AdminUsersPage';
// @ts-ignore
import AdminRecipesPage from './pages/AdminRecipesPage';
// @ts-ignore
import NotFoundPage from './pages/NotFoundPage';
import RegisterPage from './pages/RegisterPage';

// Styles
import './App.css';

// Protected Route Component với fix TypeScript
const ProtectedRoute = ({ children, requiredAuth, requiredAdmin = false }) => {
    const { isAuthenticated, currentUser, loading } = React.useContext(AuthContext);

    if (loading) {
        return <div>Đang tải...</div>;
    }

    if (requiredAuth && !isAuthenticated) {
        return <Navigate to="/login" />;
    }

    if (requiredAdmin && (!isAuthenticated || currentUser?.role !== 'admin')) {
        return <Navigate to="/" />;
    }

    return <>{children}</>;
};

const App = () => {
    return (
        <Router>
            <AuthProvider>
                <RecipeProvider>
                    <div className="app">
                        {/* Sử dụng MainLayout cho các trang người dùng */}
                        <Routes>
                            {/* Public Routes */}
                            <Route path="/login" element={<LoginPage />} />
                            <Route path="/register" element={<RegisterPage />} />

                            {/* Admin Routes - sử dụng AdminLayout */}
                            <Route path="/admin" element={
                                <ProtectedRoute requiredAuth={true} requiredAdmin={true}>
                                    <AdminLayout />
                                </ProtectedRoute>
                            }>
                                <Route index element={<AdminPage />} />
                                <Route path="users" element={<AdminUsersPage />} />
                                <Route path="recipes" element={<AdminRecipesPage />} />
                            </Route>

                            {/* Các routes thông thường - sử dụng MainLayout */}
                            <Route element={<MainLayout />}>
                                {/* Protected Routes */}
                                <Route path="/" element={<HomePage />} />

                                <Route path="/create-recipe" element={
                                    <ProtectedRoute requiredAuth={true}>
                                        <CreateRecipePage />
                                    </ProtectedRoute>
                                } />

                                <Route path="/recipe/:id" element={
                                    <ProtectedRoute requiredAuth={true}>
                                        <RecipeDetailPage />
                                    </ProtectedRoute>
                                } />

                                <Route path="/search" element={
                                    <ProtectedRoute requiredAuth={true}>
                                        <SearchResultsPage />
                                    </ProtectedRoute>
                                } />

                                <Route path="/profile/:id" element={
                                    <ProtectedRoute requiredAuth={true}>
                                        <ProfilePage />
                                    </ProtectedRoute>
                                } />

                                {/* Redirect from /create to /create-recipe */}
                                <Route path="/create" element={<Navigate replace to="/create-recipe" />} />

                                {/* Not Found Page */}
                                <Route path="*" element={<NotFoundPage />} />
                            </Route>
                        </Routes>
                    </div>
                </RecipeProvider>
            </AuthProvider>
        </Router>
    );
};

export default App;
