import React from 'react';
import { Link } from 'react-router-dom';

const Followers = (props) => {
    const followers = props.followers || []; // hoặc [] nếu undefined
    const followerCount = props.followerCount ?? followers.length ?? 0;
    const onClose = props.onClose || (() => {});

    return (
        <div className="followers-list">
            <h2>Người theo dõi</h2>
            <span>{followerCount} người theo dõi</span>
            {followers.length > 0 ? (
                <ul className="user-list">
                    {followers.map((follower) => (
                        <li key={follower.id} className="user-item">
                            <Link 
                                to={`/profile/${follower.id}`} 
                                className="user-link"
                                onClick={onClose}
                            >
                                <img 
                                    src={follower.picture ? 
                                        `http://localhost:5000${follower.picture}` : 
                                        `https://ui-avatars.com/api/?name=${encodeURIComponent(follower.name)}&background=random`
                                    } 
                                    alt={`${follower.name}'s avatar`} 
                                    className="user-avatar"
                                />
                                <span className="user-name">{follower.name}</span>
                            </Link>
                        </li>
                    ))}
                </ul>
            ) : (
                <p className="empty-message">Chưa có người theo dõi nào.</p>
            )}
        </div>
    );
};

export default Followers;
