import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getRecipeImageUrl, generateRecipeImageUrl } from '../../utils/imageUtils';
import './RecipeCard.css';

const RecipeCard = ({ recipe }) => {
  const [imageLoaded, setImageLoaded] = useState(false);
  const [imageError, setImageError] = useState(false);
  const [imageUrl, setImageUrl] = useState('');

  useEffect(() => {
    if (recipe) {
      const url = getRecipeImageUrl(recipe);
      setImageUrl(url);
      console.log(`Recipe ${recipe.id} (${recipe.title}) image URL: ${url}`);

      // Pre-load image to check if it's valid
      const img = new Image();
      img.onload = () => {
        setImageLoaded(true);
        setImageError(false);
      };
      img.onerror = () => {
        console.error(`Failed to load image for recipe: ${recipe.title}, URL: ${url}`);
        setImageError(true);
      };
      img.src = url;
    }
  }, [recipe]);

  // Tạo màu nền ngẫu nhiên nhưng ổn định cho mỗi công thức
  const getBackgroundColor = () => {
    // Sử dụng ID làm seed để có được màu ổn định cho mỗi recipe
    const seed = recipe.id || 0;
    const hue = (seed * 137.5) % 360; // Sử dụng phép nhân để tạo sự phân tán
    return `hsl(${hue}, 70%, 80%)`;
  };

  return (
    <div className="recipe-card">
      <Link to={`/recipe/${recipe.id}`}>
        <div className="recipe-image">
          {!imageError ? (
            <img 
              src={imageUrl}
              alt={recipe.title}
              onLoad={() => setImageLoaded(true)}
              onError={() => {
                console.error(`Image render error for recipe: ${recipe.title}, URL: ${imageUrl}`);
                setImageError(true);
              }}
              style={{ display: imageLoaded ? 'block' : 'none' }}
            />
          ) : null}

          {(!imageLoaded || imageError) && (
            <div 
              className="recipe-placeholder"
              style={{ 
                backgroundColor: getBackgroundColor(),
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                width: '100%',
                height: '100%',
                fontSize: '2rem',
                color: 'white'
              }}
            >
              {recipe.title.charAt(0).toUpperCase()}
            </div>
          )}
        </div>
        <div className="recipe-info">
          <h3>{recipe.title}</h3>
          <p>{recipe.cookingTime || 0} phút</p>
        </div>
      </Link>
    </div>
  );
};

export default RecipeCard;
