import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './ReportUserPage.css';

const ReportUserPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { currentUser, isAuthenticated } = useContext(AuthContext);
  
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  
  const [reportData, setReportData] = useState({
    reason: '',
    details: ''
  });
  
  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }
    
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token');
        const headers = { 'Authorization': `Bearer ${token}` };
        const response = await fetch(`http://localhost:5000/api/users/${id}`, { headers });
        
        if (!response.ok) {
          throw new Error('Không thể tải thông tin người dùng');
        }
        
        const result = await response.json();
        setUserData(result.data || result);
      } catch (err) {
        setError('Không thể tải thông tin người dùng. Vui lòng thử lại sau.');
      } finally {
        setLoading(false);
      }
    };
    
    fetchUserData();
  }, [id, isAuthenticated, navigate]);
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setReportData(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!reportData.reason) {
      setError('Vui lòng chọn lý do báo cáo');
      return;
    }
    
    try {
      setSubmitting(true);
      setError(null);
      
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/reports`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          type: 'user',
          reported_id: id,
          reason: reportData.reason,
          description: reportData.details
        })
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Không thể gửi báo cáo');
      }
      
      setSuccess(true);
      setTimeout(() => {
        navigate(`/profile/${id}`);
      }, 3000);
      
    } catch (err) {
      setError(err.message || 'Có lỗi xảy ra. Vui lòng thử lại sau.');
    } finally {
      setSubmitting(false);
    }
  };
  
  if (loading) {
    return <div className="loading-container">Đang tải...</div>;
  }
  
  return (
    <div className="report-page">
      <div className="report-container">
        <h1>Báo cáo người dùng</h1>
        
        {userData && (
          <div className="reported-user-info">
            <img 
              src={userData.picture ? `http://localhost:5000${userData.picture}` : `https://ui-avatars.com/api/?name=${encodeURIComponent(userData.name)}&background=random`} 
              alt={userData.name}
              className="reported-user-avatar"
            />
            <div className="reported-user-details">
              <h3>{userData.name}</h3>
              <p>{userData.email}</p>
            </div>
          </div>
        )}
        
        {error && (
          <div className="error-message">
            <p>{error}</p>
          </div>
        )}
        
        {success ? (
          <div className="success-message">
            <h3>Báo cáo đã được gửi!</h3>
            <p>Cảm ơn bạn đã báo cáo. Chúng tôi sẽ xem xét báo cáo của bạn.</p>
            <p>Đang quay lại trang người dùng...</p>
          </div>
        ) : (
          <form className="report-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="reason">Lý do báo cáo:</label>
              <select 
                id="reason" 
                name="reason" 
                value={reportData.reason} 
                onChange={handleChange}
                required
              >
                <option value="">-- Chọn lý do --</option>
                <option value="spam">Spam/Quảng cáo</option>
                <option value="inappropriate">Nội dung không phù hợp</option>
                <option value="harassment">Quấy rối/Xúc phạm</option>
                <option value="fake">Tài khoản giả mạo</option>
                <option value="other">Lý do khác</option>
              </select>
            </div>
            
            <div className="form-group">
              <label htmlFor="details">Chi tiết:</label>
              <textarea
                id="details"
                name="details"
                rows="5"
                value={reportData.details}
                onChange={handleChange}
                placeholder="Vui lòng cung cấp thêm chi tiết về vấn đề bạn đang gặp phải..."
              ></textarea>
            </div>
            
            <div className="form-actions">
              <button 
                type="button" 
                className="cancel-button" 
                onClick={() => navigate(`/profile/${id}`)}
              >
                Hủy
              </button>
              <button 
                type="submit" 
                className="submit-button"
                disabled={submitting}
              >
                {submitting ? 'Đang gửi...' : 'Gửi báo cáo'}
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default ReportUserPage;
