import React, { useState, useEffect, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import RecipeCard from '../components/recipe/RecipeCard';
import './HomePage.css';

const HomePage = () => {
    const { currentUser } = useContext(AuthContext);
    const navigate = useNavigate();
    const [userRecipes, setUserRecipes] = useState([]);
    const [savedRecipes, setSavedRecipes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState('my-recipes'); // Tab mặc định: 'my-recipes' hoặc 'saved'

    useEffect(() => {
        const fetchUserRecipes = async () => {
            try {
                setLoading(true);

                // Lấy token từ localStorage
                const token = localStorage.getItem('auth_token');
                if (!token || !currentUser) {
                    navigate('/login');
                    return;
                }

                const headers = { 'Authorization': `Bearer ${token}` };

                // Lấy công thức của người dùng
                const response = await fetch(`http://localhost:5000/api/recipes/user/${currentUser.id}/all`, { headers });
                if (!response.ok) {
                    throw new Error('Không thể tải công thức');
                }

                const data = await response.json();
                console.log("Fetched recipes in HomePage:", data);
                setUserRecipes(data.data || []);

                // Lấy danh sách công thức đã lưu
                const savedResponse = await fetch(`http://localhost:5000/api/recipes/saved`, { headers });
                if (!savedResponse.ok) {
                    throw new Error('Không thể tải công thức đã lưu');
                }

                const savedData = await savedResponse.json();
                console.log("Fetched saved recipes:", savedData);
                setSavedRecipes(savedData.data || []);

            } catch (err) {
                console.error('Error fetching recipes:', err);
                setError('Không thể tải công thức. Vui lòng thử lại sau.');
            } finally {
                setLoading(false);
            }
        };

        fetchUserRecipes();
    }, [currentUser, navigate]);

    if (loading) {
        return <div className="loading-spinner">Đang tải...</div>;
    }

    return (
        <div className="home-page">
            <div className="profile-section">
                <div className="profile-header" onClick={() => navigate(`/dashboard`)}>
                    <div className="profile-avatar">
                        {currentUser?.picture ? (
                            <img src={currentUser.picture} alt={currentUser.name} />
                        ) : (
                            <div className="avatar-placeholder">{currentUser?.name?.charAt(0) || "U"}</div>
                        )}
                    </div>

                    <div className="profile-info">
                        <h1>{currentUser?.name || "Người dùng"}</h1>
                        <p>{currentUser?.email || "email@example.com"}</p>
                    </div>
                </div>

                <div className="profile-stats">
                    <div className="stat-item">
                        <span className="stat-label">Người theo dõi:</span>
                        <span className="stat-value">{currentUser?.followerCount || 0}</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-label">Đang theo dõi:</span>
                        <span className="stat-value">{currentUser?.followingCount || 0}</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-label">Công thức:</span>
                        <span className="stat-value">{userRecipes.length || 0}</span>
                    </div>
                </div>
            </div>

            {/* Tabs điều hướng */}
            <div className="recipe-tabs">
                <button 
                    className={`tab-button ${activeTab === 'my-recipes' ? 'active' : ''}`} 
                    onClick={() => setActiveTab('my-recipes')}
                >
                    Công thức của tôi
                </button>
                <button 
                    className={`tab-button ${activeTab === 'saved' ? 'active' : ''}`}
                    onClick={() => setActiveTab('saved')}
                >
                    Đã lưu
                </button>
            </div>

            {/* Hiển thị công thức dựa trên tab đang chọn */}
            <div className="recipes-section">
                {activeTab === 'my-recipes' && (
                    <>
                        <h2>Công thức của tôi</h2>
                        {error && <p className="error-message">{error}</p>}

                        {userRecipes.length === 0 ? (
                            <p className="empty-message">Bạn chưa có công thức nào. Hãy tạo công thức đầu tiên!</p>
                        ) : (
                            <div className="recipes-grid">
                                {userRecipes.map(recipe => (
                                    <Link to={`/recipe/${recipe.id}`} key={recipe.id}>
                                        <RecipeCard recipe={recipe} />
                                    </Link>
                                ))}
                            </div>
                        )}
                    </>
                )}

                {activeTab === 'saved' && (
                    <>
                        <h2>Công thức đã lưu</h2>

                        {savedRecipes.length === 0 ? (
                            <p className="empty-message">Bạn chưa lưu công thức nào.</p>
                        ) : (
                            <div className="recipes-grid">
                                {savedRecipes.map(recipe => (
                                    <Link to={`/recipe/${recipe.id}`} key={recipe.id}>
                                        <RecipeCard recipe={recipe} />
                                    </Link>
                                ))}
                            </div>
                        )}
                    </>
                )}
            </div>
        </div>
    );
};

export default HomePage;
