import React from 'react';
import Followers from './Followers';
import Following from './Following';
import UserPosts from './UserPosts';

const ProfileLayout = ({
  user,
  isOwner,
  isFollowing,
  onFollowToggle,
  onEditProfile,
}) => (
  <div className="profile-page">
    <div className="profile-header">
      <img
        className="profile-avatar"
        src={user.picture?.startsWith('http') ? user.picture : `http://localhost:5000/${user.picture}`}
        alt={user.name}
      />
      <div className="profile-info">
        <h1>{user.name}</h1>
        <p>{user.email}</p>
        {/* Nút theo dõi/chỉnh sửa */}
        {!isOwner && (
          <button className="follow-button" onClick={onFollowToggle}>
            {isFollowing ? 'Đang theo dõi' : 'Theo dõi'}
          </button>
        )}
        {isOwner && (
          <button className="edit-profile-btn" onClick={onEditProfile}>
            Chỉnh sửa
          </button>
        )}
      </div>
    </div>
    <div className="profile-stats">
      <span>Người theo dõi: {user.followerCount || 0}</span>
      <span>Đang theo dõi: {user.followingCount || 0}</span>
      <span>Bài đăng: {user.recipeCount || 0}</span>
    </div>
    {/* Tabs */}
    <div className="profile-tabs">
      <button>Bài đăng đã đăng</button>
      {isOwner && <button>Bản nháp</button>}
      {isOwner && <button>Đã lưu</button>}
    </div>
    {/* Danh sách công thức */}
    <UserPosts userId={user.id} />
    {/* Followers/Following */}
    <Followers userId={user.id} />
    <Following userId={user.id} />
  </div>
);

export default ProfileLayout;
