import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import './ReportButton.css';

const ReportButton = ({ type, id, title }) => {
  const [showModal, setShowModal] = useState(false);
  const [reportData, setReportData] = useState({ reason: '', details: '' });
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  
  const navigate = useNavigate();
  const { isAuthenticated, currentUser } = useContext(AuthContext);
  
  const handleOpenModal = () => {
    if (!isAuthenticated) {
      localStorage.setItem('redirectAfterLogin', window.location.pathname);
      navigate('/login');
      return;
    }
    setShowModal(true);
  };
  
  const handleChange = (e) => {
    setReportData({
      ...reportData,
      [e.target.name]: e.target.value
    });
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setError(null);
    
    try {
      // Xác định endpoint dựa trên type
      const endpoint = type === 'recipe' 
        ? `/api/recipes/${id}/report` 
        : type === 'comment' 
          ? `/api/comments/${id}/report` 
          : `/api/users/${id}/report`;
      
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(reportData)
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Không thể gửi báo cáo');
      }
      
      setSuccess(true);
      setTimeout(() => {
        setShowModal(false);
        setReportData({ reason: '', details: '' });
        setSuccess(false);
      }, 2000);
      
    } catch (err) {
      setError(err.message || 'Đã xảy ra lỗi khi gửi báo cáo');
    } finally {
      setSubmitting(false);
    }
  };
  
  return (
    <>
      <button 
        className="report-button" 
        onClick={handleOpenModal}
        aria-label="Báo cáo vi phạm"
      >
        <i className="fas fa-flag"></i>
      </button>
      
      {showModal && (
        <div className="report-modal-overlay">
          <div className="report-modal">
            <div className="report-modal-header">
              <h3>Báo cáo {type === 'recipe' ? 'công thức' : type === 'comment' ? 'bình luận' : 'người dùng'}</h3>
              <button 
                className="close-button"
                onClick={() => setShowModal(false)}
              >
                <i className="fas fa-times"></i>
              </button>
            </div>
            
            <div className="report-modal-content">
              {success ? (
                <div className="success-message">
                  <i className="fas fa-check-circle"></i>
                  <p>Cảm ơn bạn đã gửi báo cáo! Quản trị viên sẽ xem xét báo cáo của bạn.</p>
                </div>
              ) : (
                <form onSubmit={handleSubmit}>
                  {error && (
                    <div className="error-message">
                      <i className="fas fa-exclamation-circle"></i>
                      <p>{error}</p>
                    </div>
                  )}
                  
                  <div className="form-group">
                    <label htmlFor="reason">Lý do báo cáo:</label>
                    <select 
                      id="reason"
                      name="reason"
                      value={reportData.reason}
                      onChange={handleChange}
                      required
                    >
                      <option value="">-- Chọn lý do --</option>
                      <option value="spam">Spam / Quảng cáo</option>
                      <option value="inappropriate">Nội dung không phù hợp</option>
                      <option value="harassment">Quấy rối / Xúc phạm</option>
                      <option value="violence">Bạo lực / Nguy hiểm</option>
                      <option value="copyright">Vi phạm bản quyền</option>
                      <option value="other">Lý do khác</option>
                    </select>
                  </div>
                  
                  <div className="form-group">
                    <label htmlFor="details">Chi tiết:</label>
                    <textarea
                      id="details"
                      name="details"
                      rows="4"
                      value={reportData.details}
                      onChange={handleChange}
                      placeholder="Vui lòng mô tả chi tiết lý do báo cáo..."
                      required
                    ></textarea>
                  </div>
                  
                  <div className="form-actions">
                    <button 
                      type="button" 
                      className="cancel-button" 
                      onClick={() => setShowModal(false)}
                      disabled={submitting}
                    >
                      Hủy
                    </button>
                    <button 
                      type="submit" 
                      className="submit-button"
                      disabled={submitting}
                    >
                      {submitting ? 'Đang gửi...' : 'Gửi báo cáo'}
                    </button>
                  </div>
                </form>
              )}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ReportButton;
