import React, { useState, useEffect } from 'react';
import '../../components/admin/AdminReportsPage.css';

const AdminReportsPage = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [filterStatus, setFilterStatus] = useState('all');
  const [filterType, setFilterType] = useState('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);

  const fetchReports = async (page = 1) => {
    try {
      setLoading(true);
      const token = localStorage.getItem('auth_token');
      
      // Xây dựng query parameters
      const params = new URLSearchParams();
      params.append('page', page);
      if (searchQuery) params.append('search', searchQuery);
      if (filterStatus !== 'all') params.append('status', filterStatus);
      if (filterType !== 'all') params.append('type', filterType);
      
      // Gọi API với các bộ lọc
      const response = await fetch(`http://localhost:5000/api/admin/reports?${params.toString()}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
      if (!response.ok) {
        throw new Error('Không thể tải danh sách báo cáo');
      }
      
      const data = await response.json();
      setReports(data.data || []);
      setTotalPages(data.pagination?.totalPages || 1);
      setCurrentPage(page);
    } catch (err) {
      console.error('Error fetching reports:', err);
      setError(err.message);
      
      // Dữ liệu mẫu
      setReports([
        {
          id: 1,
          report_type: 'user',
          reporter_name: 'Nguyễn Văn A',
          reporter_picture: null,
          reported_user_id: 5,
          reported_user_name: 'Người dùng bị báo cáo',
          reason: 'Spam',
          details: 'Người dùng này liên tục gửi tin nhắn spam',
          status: 'pending',
          created_at: new Date()
        },
        {
          id: 2,
          report_type: 'recipe',
          reporter_name: 'Trần Thị B',
          reporter_picture: null,
          reported_recipe_id: 12,
          reported_recipe_title: 'Công thức nấu ăn bị báo cáo',
          reason: 'Nội dung không phù hợp',
          details: 'Công thức này có nội dung không phù hợp',
          status: 'resolved',
          created_at: new Date()
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchReports(currentPage);
  }, [currentPage, filterStatus, filterType]);

  // Xử lý tìm kiếm
  const handleSearch = (e) => {
    e.preventDefault();
    setCurrentPage(1);
    fetchReports(1);
  };

  // Xử lý phê duyệt báo cáo
  const handleResolveReport = async (reportId) => {
    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`http://localhost:5000/api/admin/reports/${reportId}/resolve`, {
        method: 'PUT',
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
      if (!response.ok) {
        throw new Error('Không thể giải quyết báo cáo');
      }
      
      // Cập nhật danh sách
      setReports(reports.map(report => 
        report.id === reportId ? {...report, status: 'resolved'} : report
      ));
      
      // Đóng modal nếu đang mở
      if (showModal && selectedReport?.id === reportId) {
        setSelectedReport({...selectedReport, status: 'resolved'});
      }
    } catch (err) {
      console.error('Error resolving report:', err);
      setError(err.message);
    }
  };

  // Xử lý từ chối báo cáo
  const handleRejectReport = async (reportId) => {
    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`http://localhost:5000/api/admin/reports/${reportId}/reject`, {
        method: 'PUT',
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
      if (!response.ok) {
        throw new Error('Không thể từ chối báo cáo');
      }
      
      // Cập nhật danh sách
      setReports(reports.map(report => 
        report.id === reportId ? {...report, status: 'rejected'} : report
      ));
      
      // Đóng modal nếu đang mở
      if (showModal && selectedReport?.id === reportId) {
        setSelectedReport({...selectedReport, status: 'rejected'});
      }
    } catch (err) {
      console.error('Error rejecting report:', err);
      setError(err.message);
    }
  };

  // Xử lý chuyển trang
  const handlePageChange = (newPage) => {
    if (newPage < 1 || newPage > totalPages) return;
    setCurrentPage(newPage);
  };

  // Hiển thị chi tiết báo cáo
  const handleViewReport = (report) => {
    setSelectedReport(report);
    setShowModal(true);
  };

  if (loading && reports.length === 0) {
    return (
      <div className="admin-loading">
        <div className="spinner"></div>
        <p>Đang tải danh sách báo cáo...</p>
      </div>
    );
  }

  return (
    <div className="admin-reports">
      <div className="admin-page-header">
        <h1>Quản lý báo cáo</h1>
      </div>

      {/* Filters and Search */}
      <div className="admin-tools">
        <div className="admin-filters">
          <div className="filter">
            <label>Loại báo cáo:</label>
            <select value={filterType} onChange={(e) => {
              setFilterType(e.target.value);
              setCurrentPage(1);
            }}>
              <option value="all">Tất cả</option>
              <option value="user">Người dùng</option>
              <option value="recipe">Công thức</option>
              <option value="comment">Bình luận</option>
            </select>
          </div>
          
          <div className="filter">
            <label>Trạng thái:</label>
            <select value={filterStatus} onChange={(e) => {
              setFilterStatus(e.target.value);
              setCurrentPage(1);
            }}>
              <option value="all">Tất cả</option>
              <option value="pending">Chưa xử lý</option>
              <option value="resolved">Đã giải quyết</option>
              <option value="rejected">Đã bác bỏ</option>
            </select>
          </div>
        </div>
        
        <form className="admin-search" onSubmit={handleSearch}>
          <input 
            type="text" 
            placeholder="Tìm kiếm báo cáo..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button type="submit">
            <i className="fas fa-search"></i>
          </button>
        </form>
      </div>

      {error && (
        <div className="admin-error-message">
          <i className="fas fa-exclamation-triangle"></i>
          <span>{error}</span>
          <button onClick={() => setError(null)}>
            <i className="fas fa-times"></i>
          </button>
        </div>
      )}

      {/* Reports Table */}
      <div className="admin-table-responsive">
        <table className="admin-data-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Loại</th>
              <th>Nội dung báo cáo</th>
              <th>Người báo cáo</th>
              <th>Ngày báo cáo</th>
              <th>Trạng thái</th>
              <th>Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {reports.length === 0 ? (
              <tr>
                <td colSpan="7" className="no-data">
                  {searchQuery || filterStatus !== 'all' || filterType !== 'all' ? 
                    'Không tìm thấy báo cáo nào phù hợp' : 
                    'Chưa có báo cáo nào'
                  }
                </td>
              </tr>
            ) : (
              reports.map(report => (
                <tr key={report.id}>
                  <td>{report.id}</td>
                  <td>
                    <span className={`report-type ${report.report_type}`}>
                      {report.report_type === 'user' ? 'Người dùng' : 
                       report.report_type === 'recipe' ? 'Công thức' : 
                       report.report_type === 'comment' ? 'Bình luận' : 'Khác'}
                    </span>
                  </td>
                  <td>
                    <div className="report-reason">
                      <span className="report-reason-label">{report.reason}</span>
                      <span className="report-details">{report.details.substring(0, 50)}{report.details.length > 50 ? '...' : ''}</span>
                    </div>
                  </td>
                  <td className="reporter-info">
                    <img 
                      src={report.reporter_picture ? `http://localhost:5000${report.reporter_picture}` : `https://ui-avatars.com/api/?name=${encodeURIComponent(report.reporter_name)}&background=random`}
                      alt={report.reporter_name}
                      className="user-avatar-small"
                    />
                    <span>{report.reporter_name}</span>
                  </td>
                  <td>{new Date(report.created_at).toLocaleDateString('vi-VN')}</td>
                  <td>
                    <span className={`status-badge ${report.status}`}>
                      {report.status === 'pending' ? 'Chưa xử lý' : 
                       report.status === 'resolved' ? 'Đã giải quyết' : 'Đã bác bỏ'}
                    </span>
                  </td>
                  <td className="actions">
                    <button className="btn-view" onClick={() => handleViewReport(report)}>
                      <i className="fas fa-eye"></i>
                    </button>
                    
                    {report.status === 'pending' && (
                      <>
                        <button className="btn-approve" onClick={() => handleResolveReport(report.id)}>
                          <i className="fas fa-check"></i>
                        </button>
                        <button className="btn-reject" onClick={() => handleRejectReport(report.id)}>
                          <i className="fas fa-times"></i>
                        </button>
                      </>
                    )}
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="admin-pagination">
          <button 
            onClick={() => handlePageChange(1)} 
            disabled={currentPage === 1}
          >
            <i className="fas fa-angle-double-left"></i>
          </button>
          <button 
            onClick={() => handlePageChange(currentPage - 1)} 
            disabled={currentPage === 1}
          >
            <i className="fas fa-angle-left"></i>
          </button>
          
          <div className="pagination-info">
            Trang {currentPage} / {totalPages}
          </div>
          
          <button 
            onClick={() => handlePageChange(currentPage + 1)} 
            disabled={currentPage === totalPages}
          >
            <i className="fas fa-angle-right"></i>
          </button>
          <button 
            onClick={() => handlePageChange(totalPages)} 
            disabled={currentPage === totalPages}
          >
            <i className="fas fa-angle-double-right"></i>
          </button>
        </div>
      )}

      {/* Report Detail Modal */}
      {showModal && selectedReport && (
        <div className="admin-modal">
          <div className="modal-content">
            <div className="modal-header">
              <h2>Chi tiết báo cáo</h2>
              <button className="btn-close" onClick={() => setShowModal(false)}>
                <i className="fas fa-times"></i>
              </button>
            </div>
            
            <div className="modal-body">
              <div className="report-detail-header">
                <span className={`report-type ${selectedReport.report_type}`}>
                  {selectedReport.report_type === 'user' ? 'Báo cáo người dùng' : 
                   selectedReport.report_type === 'recipe' ? 'Báo cáo công thức' : 
                   selectedReport.report_type === 'comment' ? 'Báo cáo bình luận' : 'Khác'}
                </span>
                <span className={`status-badge ${selectedReport.status}`}>
                  {selectedReport.status === 'pending' ? 'Chưa xử lý' : 
                   selectedReport.status === 'resolved' ? 'Đã giải quyết' : 'Đã bác bỏ'}
                </span>
              </div>
              
              <div className="report-meta">
                <div className="meta-item">
                  <i className="fas fa-user"></i>
                  <span>Người báo cáo: {selectedReport.reporter_name}</span>
                </div>
                <div className="meta-item">
                  <i className="fas fa-calendar-alt"></i>
                  <span>Ngày báo cáo: {new Date(selectedReport.created_at).toLocaleDateString('vi-VN')}</span>
                </div>
                <div className="meta-item">
                  <i className="fas fa-exclamation-circle"></i>
                  <span>Lý do: {selectedReport.reason}</span>
                </div>
              </div>
              
              <h4>Chi tiết báo cáo:</h4>
              <div className="report-full-details">
                {selectedReport.details}
              </div>
              
              <h4>Nội dung bị báo cáo:</h4>
              <div className="reported-content">
                {selectedReport.report_type === 'user' && (
                  <div className="reported-user">
                    <div className="reported-user-info">
                      <h5>{selectedReport.reported_user_name}</h5>
                      <p>{selectedReport.reported_user_email}</p>
                      <a href={`/admin/users?id=${selectedReport.reported_user_id}`} className="view-more">
                        Xem thông tin người dùng
                      </a>
                    </div>
                  </div>
                )}
                
                {selectedReport.report_type === 'recipe' && (
                  <div className="reported-recipe">
                    <img 
                      src={selectedReport.reported_recipe_image ? `http://localhost:5000${selectedReport.reported_recipe_image}` : 'https://via.placeholder.com/100?text=No+Image'}
                      alt={selectedReport.reported_recipe_title}
                      className="recipe-image-medium"
                    />
                    <div className="reported-recipe-info">
                      <h5>{selectedReport.reported_recipe_title}</h5>
                      <p>Tác giả: {selectedReport.reported_recipe_author}</p>
                      <a href={`/admin/recipes?id=${selectedReport.reported_recipe_id}`} className="view-more">
                        Xem chi tiết công thức
                      </a>
                    </div>
                  </div>
                )}
                
                {selectedReport.report_type === 'comment' && (
                  <div className="reported-comment">
                    <blockquote>"{selectedReport.reported_comment_content}"</blockquote>
                    <p>Bởi: {selectedReport.reported_comment_author}</p>
                    <a href={`/recipe/${selectedReport.reported_recipe_id}?comment=${selectedReport.reported_comment_id}`} target="_blank" rel="noreferrer" className="view-more">
                      Xem bình luận trong công thức
                    </a>
                  </div>
                )}
              </div>
              
              {selectedReport.status === 'pending' && (
                <div className="modal-actions">
                  <button className="btn-reject" onClick={() => handleRejectReport(selectedReport.id)}>
                    <i className="fas fa-times"></i> Bác bỏ báo cáo
                  </button>
                  <button className="btn-approve" onClick={() => handleResolveReport(selectedReport.id)}>
                    <i className="fas fa-check"></i> Giải quyết
                  </button>
                </div>
              )}
              
              {selectedReport.status !== 'pending' && (
                <div className="report-resolution">
                  <h4>Trạng thái xử lý</h4>
                  <p className={`resolution-status ${selectedReport.status}`}>
                    {selectedReport.status === 'resolved' ? 'Báo cáo này đã được giải quyết.' : 'Báo cáo này đã bị bác bỏ.'}
                  </p>
                  {selectedReport.resolved_by && (
                    <p className="resolved-by">
                      Xử lý bởi: {selectedReport.resolved_by} vào {new Date(selectedReport.resolved_at).toLocaleDateString('vi-VN')}
                    </p>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminReportsPage;
