import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import './Comments.css';
import ReportButton from '../report/ReportButton';

const Comments = ({ recipeId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [replyingTo, setReplyingTo] = useState(null);
  const [replyText, setReplyText] = useState('');
  const { currentUser } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchComments = async () => {
      try {
        setLoading(true);
        const response = await fetch(`http://localhost:5000/api/comments/recipe/${recipeId}`);
        
        if (!response.ok) {
          throw new Error('Không thể tải bình luận');
        }
        
        const data = await response.json();
        setComments(data.data || []);
      } catch (err) {
        console.error('Error fetching comments:', err);
        setError('Không thể tải bình luận. Vui lòng thử lại sau.');
      } finally {
        setLoading(false);
      }
    };
    
    fetchComments();
  }, [recipeId]);

  const handleSubmitComment = async (e) => {
    e.preventDefault();
    
    if (!currentUser) {
      navigate('/login');
      return;
    }
    
    if (!newComment.trim()) return;
    
    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`http://localhost:5000/api/comments/recipe/${recipeId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ text: newComment })
      });
      
      if (response.ok) {
        const result = await response.json();
        
        // Thêm comment mới vào danh sách
        if (result.data) {
          setComments(prev => [result.data, ...prev]);
          setNewComment('');
        }
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Không thể đăng bình luận');
      }
    } catch (err) {
      console.error('Error posting comment:', err);
      alert(`Lỗi: ${err.message}`);
    }
  };

  const handleReply = (commentId) => {
    setReplyingTo(commentId);
    setReplyText('');
  };

  const handleCancelReply = () => {
    setReplyingTo(null);
    setReplyText('');
  };

  const handleSubmitReply = async (commentId) => {
    if (!currentUser) {
      navigate('/login');
      return;
    }
    
    if (!replyText.trim()) return;
    
    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`http://localhost:5000/api/comments/${commentId}/reply`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ text: replyText })
      });
      
      if (response.ok) {
        const result = await response.json();
        
        // Cập nhật danh sách bình luận
        setComments(prev => prev.map(comment => 
          comment.id === commentId 
            ? { ...comment, replies: [...(comment.replies || []), result.data] }
            : comment
        ));
        
        setReplyingTo(null);
        setReplyText('');
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Không thể trả lời bình luận');
      }
    } catch (err) {
      console.error('Error replying to comment:', err);
      alert(`Lỗi: ${err.message}`);
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('vi-VN', options);
  };

  if (loading) {
    return <div className="comments-loading">Đang tải bình luận...</div>;
  }

  const CommentItem = ({ comment, handleReply, replyingTo, replyText, setReplyText, handleSubmitReply, handleCancelReply, currentUser }) => (
    <div className="comment-item">
      <div className="comment-avatar">
        {comment.user_picture ? (
          <img 
            src={`http://localhost:5000${comment.user_picture}`} 
            alt={comment.user_name} 
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(comment.user_name)}&background=random`;
            }}
          />
        ) : (
          <div className="avatar-placeholder">
            {comment.user_name ? comment.user_name[0].toUpperCase() : 'U'}
          </div>
        )}
      </div>
      <div className="comment-content">
        <div className="comment-header">
          <div className="comment-author">{comment.user_name}</div>
          <div className="comment-date">{formatDate(comment.created_at)}</div>
          
          {/* Thêm nút báo cáo chỉ hiển thị khi không phải comment của mình */}
          {currentUser && currentUser.id !== comment.user_id && (
            <ReportButton 
              type="comment" 
              id={comment.id} 
              title={`Comment by ${comment.user_name}`} 
            />
          )}
        </div>
        
        <div className="comment-text">
          {comment.text}
        </div>
        
        <div className="comment-actions">
          <button 
            className="reply-button" 
            onClick={() => handleReply(comment.id)}
          >
            Trả lời
          </button>
          
          {/* Thêm chức năng báo cáo comment nếu không phải người viết comment */}
          {currentUser && currentUser.id !== comment.user_id && (
            <ReportButton 
                type="comment" 
                id={comment.id} 
                title={`Comment by ${comment.user_name}`} 
            />
          )}
        </div>
        
        {/* Reply form */}
        {replyingTo === comment.id && (
          <div className="reply-form-container">
            <textarea
              placeholder="Viết trả lời của bạn..."
              value={replyText}
              onChange={(e) => setReplyText(e.target.value)}
              rows={2}
              className="reply-textarea"
            />
            <div className="reply-actions">
              <button 
                className="cancel-reply-btn"
                onClick={handleCancelReply}
              >
                Hủy
              </button>
              <button 
                className="submit-reply-btn"
                onClick={() => handleSubmitReply(comment.id)}
                disabled={!replyText.trim()}
              >
                Trả lời
              </button>
            </div>
          </div>
        )}
        
        {/* Đệ quy render replies */}
        {comment.replies && comment.replies.length > 0 && (
          <div className="replies-container">
            {comment.replies.map(reply => (
              <CommentItem
                key={reply.id}
                comment={reply}
                handleReply={handleReply}
                replyingTo={replyingTo}
                replyText={replyText}
                setReplyText={setReplyText}
                handleSubmitReply={handleSubmitReply}
                handleCancelReply={handleCancelReply}
                currentUser={currentUser}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );

  return (
    <div className="comments-section">
      <h3>Bình luận ({comments.length})</h3>
      
      <div className="comment-form-container">
        <form onSubmit={handleSubmitComment} className="comment-form">
          <textarea
            placeholder="Chia sẻ cảm nghĩ của bạn..."
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            rows={3}
            required
          />
          <button type="submit" className="submit-comment-btn" disabled={!newComment.trim()}>
            Gửi
          </button>
        </form>
      </div>
      
      {error && <p className="error-message">{error}</p>}
      
      <div className="comments-list">
        {comments.length === 0 ? (
          <p className="no-comments-message">Chưa có bình luận nào. Hãy là người đầu tiên bình luận!</p>
        ) : (
          comments.map(comment => (
            <CommentItem
              key={comment.id}
              comment={comment}
              handleReply={handleReply}
              replyingTo={replyingTo}
              replyText={replyText}
              setReplyText={setReplyText}
              handleSubmitReply={handleSubmitReply}
              handleCancelReply={handleCancelReply}
              currentUser={currentUser}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default Comments;
