import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import './ReportButton.css';

const ReportButton = ({ type, id, title }) => {
  const [showModal, setShowModal] = useState(false);
  const [reportData, setReportData] = useState({ reason: '', details: '' });
  const [submitting, setSubmitting] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState('');
  
  const { isAuthenticated } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleReportClick = () => {
    if (!isAuthenticated) {
      localStorage.setItem('redirectAfterLogin', window.location.pathname);
      navigate('/login');
      return;
    }
    
    setShowModal(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setReportData({ ...reportData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      setSubmitting(true);
      setError('');
      
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Bạn cần đăng nhập để báo cáo');
      }
      
      // Xác định API endpoint dựa trên loại báo cáo
      let endpoint = '';
      switch (type) {
        case 'recipe':
          endpoint = `/api/recipes/${id}/report`;
          break;
        case 'user':
          endpoint = `/api/users/${id}/report`;
          break;
        case 'comment':
          endpoint = `/api/comments/${id}/report`;
          break;
        default:
          throw new Error('Loại báo cáo không hợp lệ');
      }
      
      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          reason: reportData.reason,
          details: reportData.details
        })
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Không thể gửi báo cáo');
      }
      
      setSuccess(true);
      // Đặt lại form
      setReportData({ reason: '', details: '' });
      
      // Tự động đóng sau 3 giây
      setTimeout(() => {
        setShowModal(false);
        setSuccess(false);
      }, 3000);
      
    } catch (err) {
      console.error('Error submitting report:', err);
      setError(err.message || 'Đã xảy ra lỗi khi gửi báo cáo');
    } finally {
      setSubmitting(false);
    }
  };

  let reportName = '';
  switch(type) {
    case 'recipe': 
      reportName = 'công thức';
      break;
    case 'user': 
      reportName = 'người dùng';
      break;
    case 'comment': 
      reportName = 'bình luận';
      break;
    default: 
      reportName = 'nội dung';
  }

  return (
    <>
      <button className="report-button" onClick={handleReportClick} title={`Báo cáo ${reportName}`}>
        <i className="fas fa-flag"></i>
      </button>
      
      {showModal && (
        <div className="report-modal-overlay">
          <div className="report-modal">
            <div className="report-modal-header">
              <h3>Báo cáo {reportName}</h3>
              <button className="close-button" onClick={() => setShowModal(false)}>
                <i className="fas fa-times"></i>
              </button>
            </div>
            
            <div className="report-modal-content">
              {success ? (
                <div className="success-message">
                  <i className="fas fa-check-circle"></i>
                  <p>Báo cáo của bạn đã được gửi thành công!</p>
                  <p>Cảm ơn bạn đã giúp chúng tôi cải thiện nền tảng.</p>
                </div>
              ) : (
                <form onSubmit={handleSubmit}>
                  {error && (
                    <div className="error-message">
                      <i className="fas fa-exclamation-circle"></i>
                      <span>{error}</span>
                    </div>
                  )}
                  
                  <div className="form-group">
                    <label htmlFor="report-reason">Lý do báo cáo:</label>
                    <select
                      id="report-reason"
                      name="reason"
                      value={reportData.reason}
                      onChange={handleChange}
                      required
                    >
                      <option value="">-- Chọn lý do --</option>
                      <option value="spam">Spam/Quảng cáo</option>
                      <option value="inappropriate">Nội dung không phù hợp</option>
                      <option value="offensive">Ngôn từ xúc phạm</option>
                      <option value="violence">Bạo lực/Khiêu dâm</option>
                      <option value="copyright">Vi phạm bản quyền</option>
                      <option value="other">Lý do khác</option>
                    </select>
                  </div>
                  
                  <div className="form-group">
                    <label htmlFor="report-details">Chi tiết:</label>
                    <textarea
                      id="report-details"
                      name="details"
                      value={reportData.details}
                      onChange={handleChange}
                      placeholder="Vui lòng mô tả chi tiết về vấn đề bạn gặp phải..."
                      rows="4"
                      required
                    ></textarea>
                  </div>
                  
                  <div className="info-text">
                    <i className="fas fa-info-circle"></i>
                    <p>Báo cáo của bạn sẽ được đội ngũ quản trị viên xem xét. Chúng tôi sẽ tiến hành các biện pháp phù hợp nếu nội dung vi phạm quy định.</p>
                  </div>
                  
                  <div className="form-actions">
                    <button 
                      type="button" 
                      className="cancel-button" 
                      onClick={() => setShowModal(false)}
                      disabled={submitting}
                    >
                      Hủy
                    </button>
                    <button 
                      type="submit" 
                      className="submit-button"
                      disabled={submitting}
                    >
                      {submitting ? 'Đang gửi...' : 'Gửi báo cáo'}
                    </button>
                  </div>
                </form>
              )}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ReportButton;
