import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import ProfileHeader from '../components/profile/ProfileHeader';
import UserPosts from '../components/profile/UserPosts';
import UserStats from '../components/profile/UserStats';
import Followers from '../components/profile/Followers';
import Following from '../components/profile/Following';
import useAuth from '../hooks/useAuth';

const UserProfilePage = () => {
    const { currentUser, isAuthenticated } = useAuth();
    const [loading, setLoading] = useState(true);
    const [profileData, setProfileData] = useState(null);
    const [error, setError] = useState(null);
    const { userId } = useParams();
    const navigate = useNavigate();

    // Debug
    console.log("UserProfilePage render - currentUser:", currentUser);
    console.log("UserProfilePage render - userId from params:", userId);

    useEffect(() => {
        const fetchProfileData = async () => {
            try {
                setLoading(true);
                setError(null);
                // Sử dụng currentUser nếu không có userId và người dùng đã đăng nhập
                if (!userId && currentUser) {
                    console.log("Using currentUser data:", currentUser);
                    setProfileData(currentUser);
                    setLoading(false);
                    return;
                }

                // Sử dụng userId từ URL nếu có
                if (userId) {
                    console.log("Fetching profile for userId:", userId);
                    const token = localStorage.getItem('token');
                    const headers = token ? { 'Authorization': `Bearer ${token}` } : {};

                    const response = await fetch(`http://localhost:5000/api/users/${userId}`, { headers });

                    if (!response.ok) throw new Error('Không thể tải thông tin người dùng');

                    const data = await response.json();
                    console.log("Profile data received:", data);
                    setProfileData(data.data);
                } else if (!currentUser) {
                    // Không có userId và không có currentUser
                    console.log("No userId or currentUser, redirecting to login");
                    navigate('/login');
                    return;
                }
            } catch (error) {
                console.error("Error fetching user profile:", error);
                setError('Không thể tải thông tin người dùng');
            } finally {
                setLoading(false);
            }
        };

        fetchProfileData();
    }, [userId, currentUser, navigate]);

    if (loading) {
        return <div className="loading">Đang tải...</div>;
    }

    if (error) {
        return <div className="error">{error}</div>;
    }

    if (!profileData) {
        return <div className="error">Không thể tải thông tin người dùng</div>;
    }

    return (
        <div className="user-profile-page">
            <ProfileHeader user={profileData} />
            <UserStats user={profileData} />

            {/* Chỉ render các component phụ thuộc khi có id người dùng */}
            {profileData.id && (
                <>
                    <Followers userId={profileData.id} />
                    <Following userId={profileData.id} />
                    <UserPosts userId={profileData.id} />
                </>
            )}

            {/* Debug info */}
            <details style={{margin: '20px', padding: '10px', border: '1px dashed #ccc'}}>
                <summary>Debug Info</summary>
                <pre>ProfileData: {JSON.stringify(profileData, null, 2)}</pre>
                <pre>CurrentUser: {JSON.stringify(currentUser, null, 2)}</pre>
                <pre>UserID from params: {userId || "undefined"}</pre>
            </details>
        </div>
    );
};

export default UserProfilePage;
