import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import RecipeCard from '../components/recipe/RecipeCard';
import { API_BASE_URL, ENDPOINTS } from '../config/api';
// import './DashboardPage.css';

const DashboardPage = () => {
  const { currentUser, isAuthenticated } = useContext(AuthContext);
  const navigate = useNavigate();
  
  const [publishedRecipes, setPublishedRecipes] = useState([]);
  const [drafts, setDrafts] = useState([]);
  const [savedRecipes, setSavedRecipes] = useState([]);
  const [loadingRecipes, setLoadingRecipes] = useState(false);
  const [loadingSaved, setLoadingSaved] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('draft'); // Bắt đầu với tab 'draft'

  // Kiểm tra trạng thái đăng nhập
  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
    }
  }, [isAuthenticated, navigate]);

  const fetchUserRecipes = async (status = 'all') => {
    console.log("Fetching recipes:", status);
    try {
      setLoadingRecipes(true);
      setError(null);
      
      // *** QUAN TRỌNG: THỐNG NHẤT SỬ DỤNG auth_token HOẶC token ***
      const token = localStorage.getItem('auth_token');
      
      if (!token || !currentUser?.id) {
        setLoadingRecipes(false);
        return;
      }
      
      console.log(`Fetching recipes for user: ${currentUser.id} with status: ${status}`);
      
      let endpoint;
      if (status === 'draft') {
        endpoint = ENDPOINTS.recipes.drafts;
      } else if (status === 'published') {
        endpoint = ENDPOINTS.recipes.userRecipes(currentUser.id) + '?status=published';
      } else if (status === 'all') {
        endpoint = ENDPOINTS.recipes.userRecipes(currentUser.id);
      }
      
      console.log("Calling API endpoint:", API_BASE_URL + endpoint);
      
      const response = await fetch(API_BASE_URL + endpoint, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      console.log("Received data:", data);
      
      if (status === 'draft') {
        setDrafts(data.data || []);
        console.log("Draft recipes set:", data.data?.length || 0);
      } else if (status === 'saved') {
        setSavedRecipes(data.data || []);
      } else if (status === 'all') {
        setPublishedRecipes(data.data || []);
      }
    } catch (error) {
      console.error('Error fetching recipes:', error);
      setError(`Không thể tải dữ liệu: ${error.message}`);
    } finally {
      setLoadingRecipes(false);
    }
  };

  const fetchSavedRecipes = async () => {
    console.log("Fetching saved recipes");
    try {
      setLoadingSaved(true);
      const token = localStorage.getItem('auth_token'); // Đổi thành 'auth_token'
      const response = await fetch('http://localhost:5000/api/recipes/saved', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log("Received saved recipes data:", data);
      setSavedRecipes(data.data || []);
    } catch (error) {
      console.error('Error fetching saved recipes:', error);
    } finally {
      setLoadingSaved(false);
    }
  };

  // *** QUAN TRỌNG: THÊM useEffect NÀY ***
  useEffect(() => {
    console.log("useEffect chạy: activeTab =", activeTab, "currentUser =", currentUser, "isAuthenticated =", isAuthenticated);
    if (isAuthenticated && currentUser?.id) {
      if (activeTab === 'all') {
        fetchUserRecipes('all');
      } else if (activeTab === 'published') {
        fetchUserRecipes('published');
      } else if (activeTab === 'draft') {
        fetchUserRecipes('draft');
      } else if (activeTab === 'saved') {
        fetchSavedRecipes();
      }
    }
  }, [isAuthenticated, currentUser, activeTab]); // Dependencies

  if (!currentUser) return <div>Đang tải...</div>;

  return (
    <div className="profile-page">
      {/* Header giống HomePage */}
      <div className="profile-header">
        <div className="profile-avatar">
          <img src={currentUser.avatar || `https://ui-avatars.com/api/?name=${encodeURIComponent(currentUser.name || 'U')}&size=128`} alt="avatar" />
        </div>
        <div className="profile-info">
          <h2>{currentUser.name}</h2>
          <p>{currentUser.email}</p>
          <p>ID: {currentUser.id}</p>
        </div>
      </div>

      {/* Tabs giống HomePage */}
      <div className="profile-tabs">
        <button className={activeTab === 'all' ? 'active' : ''} onClick={() => setActiveTab('all')}>Công thức của tôi</button>
        <button className={activeTab === 'saved' ? 'active' : ''} onClick={() => setActiveTab('saved')}>Đã lưu</button>
        <button className={activeTab === 'draft' ? 'active' : ''} onClick={() => setActiveTab('draft')}>Bản nháp</button>
      </div>

      {/* Nội dung từng tab (giữ nguyên logic fetch và render của DashboardPage) */}
      {activeTab === 'all' && (
        <div>
          <h3>Công thức của tôi</h3>
          {loadingRecipes ? (
            <div className="loading-spinner">Đang tải...</div>
          ) : publishedRecipes.length > 0 ? (
            <div className="recipe-grid">
              {publishedRecipes.map(recipe => (
                <RecipeCard key={recipe.id} recipe={recipe} />
              ))}
            </div>
          ) : (
            <div className="no-recipes">
              <p>Bạn chưa có công thức nào</p>
              <button onClick={() => navigate('/create-recipe')}>Tạo công thức mới</button>
            </div>
          )}
        </div>
      )}
      {activeTab === 'saved' && (
        <div>
          <h3>Công thức đã lưu</h3>
          {loadingSaved ? (
            <div className="loading-spinner">Đang tải...</div>
          ) : savedRecipes.length > 0 ? (
            <div className="recipe-grid">
              {savedRecipes.map(recipe => (
                <RecipeCard key={recipe.id} recipe={recipe} />
              ))}
            </div>
          ) : (
            <div className="no-recipes">
              <p>Bạn chưa lưu công thức nào</p>
              <button onClick={() => navigate('/')}>Khám phá công thức</button>
            </div>
          )}
        </div>
      )}
      {activeTab === 'draft' && (
        <div>
          <h3>Bản nháp</h3>
          {loadingRecipes ? (
            <div className="loading-spinner">Đang tải...</div>
          ) : drafts.length > 0 ? (
            <div className="recipe-grid">
              {drafts.map(recipe => (
                <RecipeCard key={recipe.id} recipe={recipe} />
              ))}
            </div>
          ) : (
            <div className="no-recipes">
              <p>Bạn chưa có bản nháp nào</p>
              <button onClick={() => navigate('/create-recipe')}>Tạo công thức mới</button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default DashboardPage;
