import React, { useContext, useEffect, useState } from 'react';
import { UserContext } from '../../context/UserContext';

const Following = () => {
    const { following, userId } = useContext(UserContext);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (!userId) {
            console.log("Component: No userId provided");
            setLoading(false);
            return;
        }

        // API calls...
    }, [userId]);

    // Guard clause
    if (!userId) {
        return <div className="no-data">Không có ID người dùng hợp lệ</div>;
    }

    return (
        <div className="following-list">
            <h2>Following</h2>
            {loading ? (
                <p>Loading...</p>
            ) : (
                <ul>
                    {following.map(user => (
                        <li key={user.id}>
                            <img src={user.avatar} alt={user.name} />
                            <span>{user.name}</span>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Following;
