/**
 * Cấu hình API URLs và các thiết lập liên quan đến API
 */

// Base URL cho tất cả các API requests
const API_BASE_URL = 'http://localhost:5000/api';

// Các endpoints cụ thể
const ENDPOINTS = {
  auth: {
    login: '/auth/login',
    register: '/auth/register',
    google: '/auth/google',
    me: '/auth/me',
    checkToken: '/auth/check-token',
  },
  recipes: {
    list: '/recipes',
    create: '/recipes',
    detail: (id) => `/recipes/${id}`,
    update: (id) => `/recipes/${id}`,
    delete: (id) => `/recipes/${id}`,
    featured: '/recipes/featured',
    saved: '/recipes/saved',
    userRecipes: (userId) => `/user/recipes/${userId}`,
  },
  search: {
    query: '/search',
    popular: '/search/popular',
    suggestions: '/search/suggestions',
  },
  notifications: {
    list: '/notifications',
    unreadCount: '/notifications/unread-count',
    markAsRead: (id) => `/notifications/${id}/read`,
    markAllAsRead: '/notifications/markAllRead',
  },
  user: {
    profile: (id) => `/users/${id}`,
    update: '/users/profile',
    avatar: '/users/avatar',
    savedRecipes: '/user/saved-recipes',
  },
  admin: {
    users: '/admin/users',
    recipes: '/admin/recipes',
    comments: '/admin/comments',
    reports: '/admin/reports',
    settings: '/admin/settings',
  },
  comments: {
    list: (recipeId) => `/comments/recipe/${recipeId}`,
    add: (recipeId) => `/comments/recipe/${recipeId}`,
    delete: (id) => `/comments/${id}`,
  },
  debug: {
    routes: '/routes',
  }
};

// Thiết lập headers mặc định
const defaultHeaders = {
  'Content-Type': 'application/json',
};

// Helper function để lấy Authorization header dựa trên token
const getAuthHeader = () => {
  const token = localStorage.getItem('token');
  return token ? { 'Authorization': `Bearer ${token}` } : {};
};

export {
  API_BASE_URL,
  ENDPOINTS,
  defaultHeaders,
  getAuthHeader
};

export default API_BASE_URL;
