import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './NotificationsPage.css';

const NotificationsPage = () => {
  const { currentUser } = useContext(AuthContext);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('all');
  const [unreadCount, setUnreadCount] = useState(0);
  const [showReplyModal, setShowReplyModal] = useState(false);
  const [showMessageModal, setShowMessageModal] = useState(false);
  const [selectedNotification, setSelectedNotification] = useState(null);
  const [replyText, setReplyText] = useState('');
  const [adminMessage, setAdminMessage] = useState('');
  const [submitting, setSubmitting] = useState(false);
  
  useEffect(() => {
    fetchNotifications();
  }, [currentUser]);
  
  const fetchNotifications = async () => {
    try {
      setLoading(true);
      
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Bạn cần đăng nhập để xem thông báo');
      }
      
      const response = await fetch('http://localhost:5000/api/notifications', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (!response.ok) {
        throw new Error('Không thể tải thông báo');
      }
      
      const data = await response.json();
      setNotifications(data.data || []);
      
      // Đếm số thông báo chưa đọc
      const unread = data.data ? data.data.filter(n => !n.is_read).length : 0;
      setUnreadCount(unread);
    } catch (error) {
      console.error('Error fetching notifications:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };
  
  const handleMarkAsRead = async (id) => {
    try {
      const token = localStorage.getItem('token');
      
      await fetch(`http://localhost:5000/api/notifications/${id}/read`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      // Cập nhật trạng thái là đã đọc
      setNotifications(notifications.map(n => 
        n.id === id ? { ...n, is_read: 1 } : n
      ));
      
      // Cập nhật số thông báo chưa đọc
      setUnreadCount(prev => prev - 1);
    } catch (error) {
      console.error('Error marking notification as read:', error);
    }
  };
  
  const handleMarkAllAsRead = async () => {
    try {
      const token = localStorage.getItem('token');
      
      await fetch('http://localhost:5000/api/notifications/read-all', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      // Cập nhật tất cả là đã đọc
      setNotifications(notifications.map(n => ({ ...n, is_read: 1 })));
      setUnreadCount(0);
    } catch (error) {
      console.error('Error marking all as read:', error);
    }
  };
  
  const handleReplyClick = (notification) => {
    setSelectedNotification(notification);
    setReplyText('');
    setShowReplyModal(true);
  };
  
  const handleMessageClick = () => {
    setShowMessageModal(true);
    setAdminMessage('');
  };
  
  const handleSubmitReply = async (e) => {
    e.preventDefault();
    
    if (!replyText.trim()) return;
    
    try {
      setSubmitting(true);
      
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/notifications/${selectedNotification.id}/reply`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ reply: replyText })
      });
      
      if (!response.ok) {
        throw new Error('Không thể gửi phản hồi');
      }
      
      // Cập nhật thông báo với phản hồi mới
      setNotifications(notifications.map(n => 
        n.id === selectedNotification.id 
          ? { ...n, user_reply: replyText, replied_at: new Date().toISOString() } 
          : n
      ));
      
      alert('Đã gửi phản hồi thành công!');
      setShowReplyModal(false);
    } catch (error) {
      console.error('Error sending reply:', error);
      alert(`Lỗi: ${error.message}`);
    } finally {
      setSubmitting(false);
    }
  };
  
  const handleSubmitMessage = async (e) => {
    e.preventDefault();
    
    if (!adminMessage.trim()) return;
    
    try {
      setSubmitting(true);
      
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/notifications/message-admin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ 
          subject: 'Tin nhắn từ người dùng',
          message: adminMessage 
        })
      });
      
      if (!response.ok) {
        throw new Error('Không thể gửi tin nhắn');
      }
      
      alert('Đã gửi tin nhắn cho quản trị viên thành công!');
      setShowMessageModal(false);
    } catch (error) {
      console.error('Error sending message to admin:', error);
      alert(`Lỗi: ${error.message}`);
    } finally {
      setSubmitting(false);
    }
  };
  
  const filteredNotifications = activeTab === 'all' 
    ? notifications 
    : activeTab === 'unread' 
      ? notifications.filter(n => !n.is_read) 
      : notifications.filter(n => n.type.includes(activeTab));
  
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('vi-VN', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  };
  
  const getNotificationIcon = (type) => {
    if (type.includes('like')) return <i className="fas fa-heart"></i>;
    if (type.includes('save')) return <i className="fas fa-bookmark"></i>;
    if (type.includes('comment')) return <i className="fas fa-comment"></i>;
    if (type.includes('report')) return <i className="fas fa-flag"></i>;
    if (type.includes('admin')) return <i className="fas fa-shield-alt"></i>;
    if (type.includes('account')) return <i className="fas fa-user-lock"></i>;
    return <i className="fas fa-bell"></i>;
  };
  
  const getNotificationLink = (notification) => {
    if (notification.reference_type === 'recipe')
      return `/recipe/${notification.reference_id}`;
    if (notification.reference_type === 'user')
      return `/profile/${notification.reference_id}`;
    return null;
  };
  
  return (
    <div className="notifications-page">
      <div className="notifications-header">
        <h1>Thông báo</h1>
        <div className="notifications-actions">
          <button 
            className="mark-all-read-btn"
            onClick={handleMarkAllAsRead}
            disabled={unreadCount === 0}
          >
            Đánh dấu tất cả đã đọc
          </button>
          <button 
            className="message-admin-btn"
            onClick={handleMessageClick}
          >
            <i className="fas fa-paper-plane"></i> Gửi tin nhắn cho quản trị viên
          </button>
        </div>
      </div>
      
      <div className="notifications-tabs">
        <button 
          className={activeTab === 'all' ? 'active' : ''}
          onClick={() => setActiveTab('all')}
        >
          Tất cả
        </button>
        <button 
          className={activeTab === 'unread' ? 'active' : ''}
          onClick={() => setActiveTab('unread')}
        >
          Chưa đọc {unreadCount > 0 && <span className="badge">{unreadCount}</span>}
        </button>
        <button 
          className={activeTab === 'like' ? 'active' : ''}
          onClick={() => setActiveTab('like')}
        >
          Lượt thích
        </button>
        <button 
          className={activeTab === 'comment' ? 'active' : ''}
          onClick={() => setActiveTab('comment')}
        >
          Bình luận
        </button>
        <button 
          className={activeTab === 'admin' ? 'active' : ''}
          onClick={() => setActiveTab('admin')}
        >
          Từ quản trị viên
        </button>
      </div>
      
      <div className="notifications-list">
        {loading ? (
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>Đang tải thông báo...</p>
          </div>
        ) : error ? (
          <div className="error-message">
            <i className="fas fa-exclamation-triangle"></i>
            <p>{error}</p>
          </div>
        ) : filteredNotifications.length === 0 ? (
          <div className="empty-notifications">
            <i className="far fa-bell-slash"></i>
            <p>Chưa có thông báo nào</p>
          </div>
        ) : (
          filteredNotifications.map(notification => (
            <div 
              key={notification.id} 
              className={`notification-item ${!notification.is_read ? 'unread' : ''}`}
              onClick={() => !notification.is_read && handleMarkAsRead(notification.id)}
            >
              <div className="notification-icon">
                {getNotificationIcon(notification.type)}
              </div>
              
              <div className="notification-content">
                <h3>{notification.title}</h3>
                <p className="notification-text">{notification.content}</p>
                
                <div className="notification-meta">
                  <span className="notification-time">{formatDate(notification.created_at)}</span>
                  
                  {/* Chỉ hiển thị link cho thông báo có tham chiếu */}
                  {getNotificationLink(notification) && (
                    <Link to={getNotificationLink(notification)} className="notification-link">
                      Xem chi tiết <i className="fas fa-chevron-right"></i>
                    </Link>
                  )}
                  
                  {/* Chỉ hiển thị nút phản hồi cho thông báo từ admin */}
                  {notification.type.includes('admin') && (
                    <button 
                      className="reply-button"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleReplyClick(notification);
                      }}
                    >
                      <i className="fas fa-reply"></i> Phản hồi
                    </button>
                  )}
                </div>
                
                {/* Hiển thị phản hồi trước đó nếu có */}
                {notification.user_reply && (
                  <div className="user-reply">
                    <p><strong>Phản hồi của bạn:</strong> {notification.user_reply}</p>
                    <small>{notification.replied_at && formatDate(notification.replied_at)}</small>
                  </div>
                )}
              </div>
            </div>
          ))
        )}
      </div>
      
      {/* Modal phản hồi cho admin */}
      {showReplyModal && selectedNotification && (
        <div className="modal-overlay" onClick={() => setShowReplyModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Phản hồi tới Admin</h3>
              <button onClick={() => setShowReplyModal(false)} className="close-button">
                <i className="fas fa-times"></i>
              </button>
            </div>
            
            <div className="modal-body">
              <div className="original-message">
                <h4>{selectedNotification.title}</h4>
                <p>{selectedNotification.content}</p>
              </div>
              
              <form onSubmit={handleSubmitReply}>
                <div className="form-group">
                  <label htmlFor="reply">Phản hồi của bạn:</label>
                  <textarea 
                    id="reply" 
                    rows="4" 
                    value={replyText}
                    onChange={(e) => setReplyText(e.target.value)}
                    placeholder="Nhập phản hồi của bạn..."
                    required
                  ></textarea>
                </div>
                
                <div className="form-actions">
                  <button 
                    type="button" 
                    className="cancel-button" 
                    onClick={() => setShowReplyModal(false)}
                    disabled={submitting}
                  >
                    Hủy
                  </button>
                  <button 
                    type="submit" 
                    className="submit-button"
                    disabled={submitting || !replyText.trim()}
                  >
                    {submitting ? 'Đang gửi...' : 'Gửi phản hồi'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
      
      {/* Modal gửi tin nhắn tới admin */}
      {showMessageModal && (
        <div className="modal-overlay" onClick={() => setShowMessageModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Gửi tin nhắn tới Admin</h3>
              <button onClick={() => setShowMessageModal(false)} className="close-button">
                <i className="fas fa-times"></i>
              </button>
            </div>
            
            <div className="modal-body">
              <form onSubmit={handleSubmitMessage}>
                <div className="form-group">
                  <label htmlFor="admin-message">Nội dung tin nhắn:</label>
                  <textarea 
                    id="admin-message" 
                    rows="6" 
                    value={adminMessage}
                    onChange={(e) => setAdminMessage(e.target.value)}
                    placeholder="Nhập tin nhắn bạn muốn gửi tới Admin..."
                    required
                  ></textarea>
                </div>
                
                <div className="info-text">
                  <i className="fas fa-info-circle"></i>
                  <p>Tin nhắn này sẽ được gửi trực tiếp tới đội ngũ quản trị viên.</p>
                </div>
                
                <div className="form-actions">
                  <button 
                    type="button" 
                    className="cancel-button" 
                    onClick={() => setShowMessageModal(false)}
                    disabled={submitting}
                  >
                    Hủy
                  </button>
                  <button 
                    type="submit" 
                    className="submit-button"
                    disabled={submitting || !adminMessage.trim()}
                  >
                    {submitting ? 'Đang gửi...' : 'Gửi tin nhắn'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default NotificationsPage;
