import React from 'react';

const Followers = (props) => {
    const followers = props.followers || []; // hoặc [] nếu undefined
    const followerCount = props.followerCount ?? followers.length ?? 0;

    return (
        <div className="followers-list">
            <h2>Followers</h2>
            <span>{followerCount} followers</span>
            {followers.length > 0 ? (
                <ul>
                    {followers.map((follower) => (
                        <li key={follower.id}>
                            <img src={follower.avatar} alt={`${follower.name}'s avatar`} />
                            <span>{follower.name}</span>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No followers yet.</p>
            )}
        </div>
    );
};

export default Followers;
