import React, { useState, useEffect, useContext, useRef } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './ProfilePage.css';

const ProfilePage = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { currentUser, isAuthenticated } = useContext(AuthContext);
    
    // Tạo ref với cấu trúc đơn giản hơn
    const initialLoadDone = useRef(false);
    
    const [profileData, setProfileData] = useState(null);
    const [recipes, setRecipes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Effect để tải dữ liệu khi component mount hoặc id thay đổi
    useEffect(() => {
        // Kiểm tra trạng thái đăng nhập
        if (!isAuthenticated) {
            navigate('/login');
            return;
        }
        
        // Luôn tải dữ liệu khi vào trang hoặc khi id thay đổi
        const fetchProfileData = async () => {
            try {
                console.log(`Fetching profile data for user ID: ${id}`);
                setLoading(true);
                setError(null);
                
                // Lấy token từ localStorage
                const token = localStorage.getItem('auth_token');
                const headers = token ? { 'Authorization': `Bearer ${token}` } : {};
                
                // Lấy thông tin profile
                const profileResponse = await fetch(`http://localhost:5000/api/users/${id}`, {
                    headers
                });
                
                if (!profileResponse.ok) {
                    throw new Error('Không thể tải thông tin người dùng');
                }
                
                const profileResult = await profileResponse.json();
                setProfileData(profileResult.data || profileResult);
                
                // Lấy công thức - sử dụng endpoint đặc biệt cho trang profile
                console.log(`Fetching recipes for user ID: ${id}`);
                const recipesResponse = await fetch(
                    `http://localhost:5000/api/recipes/user/${id}/all`, 
                    { headers }
                );
                
                if (!recipesResponse.ok) {
                    throw new Error(`Không thể tải công thức: ${recipesResponse.status}`);
                }
                
                const recipesData = await recipesResponse.json();
                console.log("Fetched recipes:", recipesData);
                
                if (recipesData.data) {
                    setRecipes(recipesData.data);
                } else if (Array.isArray(recipesData)) {
                    setRecipes(recipesData);
                } else {
                    setRecipes([]);
                }
                
                // Đánh dấu đã tải dữ liệu
                initialLoadDone.current = true;
                
            } catch (err) {
                console.error("Error loading profile data:", err);
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };
        
        fetchProfileData();
    }, [id, isAuthenticated, navigate]);

    const handleImageError = (event) => {
        event.target.src = '/default-recipe.jpg';
    };

    const handleRetry = () => {
        initialLoadDone.current = false; // Reset trạng thái để tải lại
        window.location.reload();
    };

    if (loading) {
        return <div className="loading">Đang tải...</div>;
    }

    return (
        <div className="profile-page">
            <div className="profile-header">
                <img 
                    src={profileData?.picture ? `http://localhost:5000${profileData.picture}` : '/default-avatar.jpg'} 
                    alt={profileData?.name || 'Người dùng'} 
                    className="profile-avatar"
                    onError={handleImageError}
                />
                <div className="profile-info">
                    <h1>{profileData?.name || 'Người dùng'}</h1>
                    <p>{profileData?.email || ''}</p>
                    {currentUser && profileData && currentUser.id === parseInt(profileData.id) && (
                        <button 
                            className="edit-profile-button"
                            onClick={() => navigate('/profile/edit')}
                        >
                            Chỉnh sửa thông tin
                        </button>
                    )}
                </div>
            </div>
            
            <div className="profile-stats">
                <div className="stat">
                    <span className="stat-value">{recipes?.length || 0}</span>
                    <span className="stat-label">Công thức</span>
                </div>
                <div className="stat">
                    <span className="stat-value">{profileData?.total_likes || 0}</span>
                    <span className="stat-label">Lượt thích</span>
                </div>
                <div className="stat">
                    <span className="stat-value">{profileData?.total_saves || 0}</span>
                    <span className="stat-label">Lượt lưu</span>
                </div>
            </div>
            
            <h2>Công thức của {profileData?.name || 'tôi'}</h2>
            
            {error ? (
                <div className="error-message">
                    {error}
                    <button 
                        onClick={handleRetry}
                        className="retry-button"
                    >
                        Thử lại
                    </button>
                </div>
            ) : recipes?.length === 0 ? (
                <div>
                    <p>Chưa có công thức nào.</p>
                    {currentUser && profileData && currentUser.id === parseInt(profileData.id) && (
                        <button 
                            className="create-recipe-button"
                            onClick={() => navigate('/create-recipe')}
                        >
                            Tạo công thức mới
                        </button>
                    )}
                </div>
            ) : (
                <div className="recipe-grid">
                    {recipes.map(recipe => (
                        <Link to={`/recipe/${recipe.id}`} key={recipe.id}>
                            <div className="recipe-card">
                                <div className="recipe-image">
                                    <img 
                                        src={recipe.image_url ? `http://localhost:5000${recipe.image_url}` : '/default-recipe.jpg'} 
                                        alt={recipe.title}
                                        onError={(e) => {e.target.src = '/default-recipe.jpg'}}
                                    />
                                </div>
                                <div className="recipe-content">
                                    <h3>{recipe.title}</h3>
                                    <p>{recipe.cooking_time} phút</p>
                                </div>
                            </div>
                        </Link>
                    ))}
                </div>
            )}
        </div>
    );
};

export default ProfilePage;
