import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './RecipeCard.css';

const RecipeCard = ({ recipe }) => {
  const [imageError, setImageError] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);

  // Tạo URL hình ảnh đầy đủ
  const getImageUrl = () => {
    if (!recipe.image_url) {
      return null;
    }

    // Đảm bảo URL luôn bắt đầu bằng http://localhost:5000
    if (recipe.image_url.startsWith('http')) {
      return recipe.image_url;
    }

    // Đảm bảo đường dẫn có / ở đầu
    const path = recipe.image_url.startsWith('/') ? recipe.image_url : `/${recipe.image_url}`;
    return `http://localhost:5000${path}`;
  };

  // Kiểm tra xem recipe có ảnh không
  const hasImage = !!recipe.image_url;

  // Lấy URL ảnh
  const imageUrl = getImageUrl();

  // Tạo màu nền ngẫu nhiên nhưng ổn định cho placeholder
  const getBackgroundColor = () => {
    const seed = recipe.id || 0;
    const hue = (seed * 137.5) % 360;
    return `hsl(${hue}, 70%, 80%)`;
  };

  // Preload hình ảnh
  useEffect(() => {
    if (imageUrl) {
      const img = new Image();
      img.onload = () => setImageLoaded(true);
      img.onerror = (e) => {
        console.error(`Failed to preload image for recipe ${recipe.id} (${recipe.title}):`, imageUrl);
        setImageError(true);
      };
      img.crossOrigin = "anonymous";
      img.src = imageUrl;
    }
  }, [recipe.id, imageUrl, recipe.title]);

  return (
    <div className="recipe-card">
      <Link to={`/recipe/${recipe.id}`}>
        <div className="recipe-image-container">
          {hasImage && !imageError ? (
            <img
              src={imageUrl}
              alt={recipe.title}
              crossOrigin="anonymous"
              onLoad={() => setImageLoaded(true)}
              onError={(e) => {
                console.error(`Image load error for recipe ${recipe.id} (${recipe.title}):`, imageUrl);
                setImageError(true);
              }}
              style={{
                display: imageLoaded ? 'block' : 'none'
              }}
            />
          ) : null}

          {(!imageLoaded || imageError) && (
            <div 
              className="recipe-image-placeholder"
              style={{ 
                backgroundColor: getBackgroundColor(),
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                width: '100%',
                height: '100%',
                fontSize: '2rem',
                color: 'white'
              }}
            >
              <span className="placeholder-text">{recipe.title.charAt(0).toUpperCase()}</span>
            </div>
          )}
        </div>
        <div className="recipe-info">
          <h3>{recipe.title}</h3>
          <p>{recipe.cooking_time || recipe.cookingTime || 0} phút</p>
        </div>
      </Link>
    </div>
  );
};

export default RecipeCard;
