import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { getRecipeImageUrl } from '../../utils/imageUtils';
import './RecipeCard.css';

const RecipeCard = ({ recipe }) => {
  const [imageError, setImageError] = useState(false);

  const getBackgroundColor = () => {
    const seed = recipe.id || 0;
    const hue = (seed * 137.5) % 360;
    return `hsl(${hue}, 70%, 80%)`;
  };

  // Tạo URL hình ảnh
  const imageUrl = getRecipeImageUrl(recipe);

  return (
    <div className="recipe-card">
      <Link to={`/recipe/${recipe.id}`} className="recipe-link">
        <div className="recipe-image-container">
          {!imageError ? (
            <img 
              src={imageUrl}
              alt={recipe.title}
              onError={(e) => {
                console.error(`Failed to load image for recipe ${recipe.id}: ${imageUrl}`);
                setImageError(true);
              }}
            />
          ) : (
            <div 
              className="recipe-image-placeholder"
              style={{ 
                backgroundColor: getBackgroundColor(),
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                width: '100%',
                height: '100%'
              }}
            >
              <span>{recipe.title ? recipe.title.charAt(0).toUpperCase() : 'R'}</span>
            </div>
          )}
        </div>
        <div className="recipe-info">
          <h3>{recipe.title}</h3>
          <p>{recipe.cookingTime || 0} phút</p>
        </div>
      </Link>
    </div>
  );
};

export default RecipeCard;
