import React from 'react';
// Import tiện ích
import { getRecipeImageUrl } from '../../utils/imageUtils';
import { useStableColor } from '../../hooks/useStableColor';

const RecipeCard = ({ recipe, onClick }) => {
    // Tạo màu ổn định cho mỗi công thức dựa trên ID
    const backgroundColor = useStableColor(recipe.id);

    // Sử dụng trong trường hợp không có ảnh
    const placeholderStyle = {
        width: '100%',
        height: '200px',
        backgroundColor,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#fff',
        fontSize: '18px',
        fontWeight: 'bold',
        textAlign: 'center',
        padding: '20px',
        borderRadius: '8px'
    };

    return (
        <div className="recipe-card" onClick={onClick}>
            <div className="recipe-image">
                {recipe.image_url ? (
                    <img 
                        src={getRecipeImageUrl(recipe)} 
                        alt={recipe.title}
                        onError={(e) => {
                            e.target.style.display = 'none';
                            e.target.nextSibling.style.display = 'flex';
                        }}
                    />
                ) : (
                    <div style={placeholderStyle}>
                        {recipe.title}
                    </div>
                )}
            </div>
            <div className="recipe-content">
                <h3 className="recipe-title">{recipe.title}</h3>
                <p className="recipe-summary">{recipe.summary}</p>
                <div className="recipe-info">
                    <span className="recipe-time">{recipe.cookingTime} mins</span>
                    <span className="recipe-servings">{recipe.servings} servings</span>
                </div>
            </div>
        </div>
    );
};

export default RecipeCard;
