import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Following = ({ userId, onClose = () => {} }) => {
    const [following, setFollowing] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (!userId) {
            setLoading(false);
            return;
        }
        // Gọi API lấy danh sách following
        fetch(`http://localhost:5000/api/users/${userId}/following`)
            .then(res => res.json())
            .then(data => {
                setFollowing(data.data || []);
                setLoading(false);
            })
            .catch(() => setLoading(false));
    }, [userId]);

    if (!userId) return <div className="no-data">Không có ID người dùng hợp lệ</div>;

    return (
        <div className="following-list">
            <h2>Đang theo dõi</h2>
            {loading ? (
                <p>Đang tải...</p>
            ) : following.length > 0 ? (
                <ul className="user-list">
                    {following.map(user => (
                        <li key={user.id} className="user-item">
                            <Link 
                                to={`/profile/${user.id}`}
                                className="user-link"
                                onClick={onClose}
                            >
                                <img 
                                    src={user.picture ? 
                                        `http://localhost:5000${user.picture}` : 
                                        `https://ui-avatars.com/api/?name=${encodeURIComponent(user.name)}&background=random`
                                    }
                                    alt={user.name} 
                                    className="user-avatar"
                                />
                                <span className="user-name">{user.name}</span>
                            </Link>
                        </li>
                    ))}
                </ul>
            ) : (
                <p className="empty-message">Chưa theo dõi ai.</p>
            )}
        </div>
    );
};

const Profile = ({ profileData }) => {
    if (!profileData) {
        return <div>Đang tải thông tin người dùng...</div>;
    }
    return (
        <div className="profile">
            <h1>{profileData.name}</h1>
            <Following userId={profileData.id} />
        </div>
    );
};

export default Profile;
