import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Following = ({ userId, following = [], onClose = () => {} }) => {
    // Nếu có prop following truyền vào, dùng luôn mà không cần fetch API
    return (
        <div className="following-list">
            <h2>Đang theo dõi</h2>
            {following.length > 0 ? (
                <ul className="user-list">
                    {following.map(user => (
                        <li key={user.id} className="user-item">
                            <Link 
                                to={`/profile/${user.id}`}
                                className="user-link"
                                onClick={onClose}
                            >
                                <img 
                                    src={user.picture ? 
                                        (user.picture.startsWith('http') ? user.picture : `http://localhost:5000${user.picture}`) : 
                                        `https://ui-avatars.com/api/?name=${encodeURIComponent(user.name)}&background=random`
                                    }
                                    alt={user.name} 
                                    className="user-avatar"
                                />
                                <span className="user-name">{user.name}</span>
                            </Link>
                        </li>
                    ))}
                </ul>
            ) : (
                <p className="empty-message">Chưa theo dõi ai.</p>
            )}
        </div>
    );
};

const Profile = ({ profileData }) => {
    if (!profileData) {
        return <div>Đang tải thông tin người dùng...</div>;
    }
    return (
        <div className="profile">
            <h1>{profileData.name}</h1>
            <Following userId={profileData.id} />
        </div>
    );
};

export default Profile;
