import React, { useContext, useState, useCallback, memo } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import './Header.css';

// Import tiện ích
import { generateAvatarUrl } from '../../utils/imageUtils';

// Sử dụng memo để tránh re-render không cần thiết
const Header = memo(() => {
    const { currentUser, logout, isAuthenticated } = useContext(AuthContext);
    const [searchQuery, setSearchQuery] = useState('');
    const navigate = useNavigate();
    const [avatarLoaded, setAvatarLoaded] = useState(false);

    // Xác định trước các giá trị để tránh tính toán lại khi render
    const userName = currentUser?.name || 'Người dùng';
    const userAvatar = currentUser?.picture 
                  ? `http://localhost:5000${currentUser.picture}` 
                  : generateAvatarUrl(userName);
    const userId = currentUser?.id;
    const logoLink = isAuthenticated ? "/dashboard" : "/";

    // Sử dụng useCallback để không tạo lại hàm mỗi khi render
    const handleSearch = useCallback((e) => {
        e.preventDefault();
        if (searchQuery.trim()) {
            navigate(`/search?q=${encodeURIComponent(searchQuery)}`);
        }
    }, [searchQuery, navigate]);

    const handleLogout = useCallback(() => {
        localStorage.removeItem('auth_token');
        localStorage.removeItem('user');
        localStorage.removeItem('profile_data_timestamp');
        logout();
        navigate('/home');
    }, [logout, navigate]);

    // Xử lý sự kiện khi ảnh tải xong
    const handleImageLoad = () => {
        setAvatarLoaded(true);
    };

    const handleImageError = (e) => {
        e.target.src = generateAvatarUrl(userName);
        setAvatarLoaded(true);
    };

    return (
        <header className="main-header">
            <div className="header-container">
                <div className="header-logo">
                    <Link to={logoLink}>Recipe Sharing</Link>
                </div>

                <div className="search-bar">
                    <form onSubmit={handleSearch}>
                        <input
                            type="text"
                            placeholder="Tìm kiếm công thức..."
                            value={searchQuery}
                            onChange={e => setSearchQuery(e.target.value)}
                        />
                        <button type="submit">
                            <i className="fas fa-search"></i>
                        </button>
                    </form>
                </div>

                <nav className="header-nav">
                    {isAuthenticated ? (
                        <>
                            <Link to="/create-recipe">Tạo công thức</Link>
                            <div className="user-menu">
                                {currentUser && (
                                    <Link to={`/dashboard`} className="user-profile-link">
                                        <div className="avatar-container" style={{ width: '32px', height: '32px', position: 'relative' }}>
                                            {!avatarLoaded && (
                                                <div style={{ position: 'absolute', top: 0, left: 0, width: '32px', height: '32px', backgroundColor: '#f0f0f0', borderRadius: '50%' }} />
                                            )}
                                            {currentUser?.picture ? (
                                                <img 
                                                    src={`http://localhost:5000${currentUser.picture}`}
                                                    alt={userName}
                                                    className="avatar-small"
                                                    onLoad={handleImageLoad}
                                                    onError={(e) => {
                                                        setAvatarLoaded(true);
                                                        e.target.style.display = 'none';
                                                        e.target.parentNode.appendChild(
                                                            Object.assign(document.createElement('div'), {
                                                                className: 'avatar-placeholder',
                                                                style: `background-color: ${useStableColor(currentUser.id)}`,
                                                                innerText: userName.charAt(0).toUpperCase()
                                                            })
                                                        );
                                                    }}
                                                    style={{ opacity: avatarLoaded ? 1 : 0 }}
                                                />
                                            ) : (
                                                <div 
                                                    className="avatar-placeholder" 
                                                    style={{ backgroundColor: useStableColor(currentUser?.id) }}
                                                >
                                                    {userName.charAt(0).toUpperCase()}
                                                </div>
                                            )}
                                        </div>
                                        <span>{userName}</span>
                                    </Link>
                                )}
                                <button onClick={handleLogout} className="logout-btn">Đăng xuất</button>
                            </div>
                        </>
                    ) : (
                        <>
                            <Link to="/login" className="nav-login">Đăng nhập</Link>
                            <Link to="/register" className="nav-register">Đăng ký</Link>
                        </>
                    )}
                </nav>
            </div>
        </header>
    );
});

export default Header;
